@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&family=JetBrains+Mono:wght@400;500;600&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Core theme colors matching spec */
  --background: #0B0F14;
  --foreground: hsl(210, 40%, 98%);
  --card: #111827;
  --card-foreground: hsl(210, 40%, 98%);
  --popover: #111827;
  --popover-foreground: hsl(210, 40%, 98%);
  --primary: #7C3AED;
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: #1F2937;
  --secondary-foreground: hsl(210, 40%, 98%);
  --muted: #1F2937;
  --muted-foreground: hsl(210, 40%, 90%);
  --accent: #22D3EE;
  --accent-foreground: #0B0F14;
  --destructive: #EF4444;
  --destructive-foreground: hsl(210, 40%, 98%);
  --border: #1F2937;
  --input: #1F2937;
  --ring: #7C3AED;

  /* Design system colors */
  --success: #10B981;
  --warning: #F59E0B;
  --danger: #EF4444;

  /* Chart colors */
  --chart-1: #7C3AED;
  --chart-2: #22D3EE;
  --chart-3: #10B981;
  --chart-4: #F59E0B;
  --chart-5: #EF4444;

  /* Glass morphism */
  --glass: rgba(255, 255, 255, 0.05);
  --glass-border: rgba(255, 255, 255, 0.1);

  /* Typography */
  --font-sans: 'Inter', system-ui, sans-serif;
  --font-mono: 'JetBrains Mono', monospace;
  --font-display: 'Inter', system-ui, sans-serif;

  /* Layout */
  --radius: 1rem;
  --radius-lg: 1.25rem;
}

.dark {
  /* Dark theme inherits root colors */
  --background: #0B0F14;
  --foreground: hsl(210, 40%, 98%);
  --card: #111827;
  --card-foreground: hsl(210, 40%, 98%);
  --popover: #111827;
  --popover-foreground: hsl(210, 40%, 98%);
  --primary: #7C3AED;
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: #1F2937;
  --secondary-foreground: hsl(210, 40%, 98%);
  --muted: #1F2937;
  --muted-foreground: hsl(210, 40%, 90%);
  --accent: #22D3EE;
  --accent-foreground: #0B0F14;
  --destructive: #EF4444;
  --destructive-foreground: hsl(210, 40%, 98%);
  --border: #1F2937;
  --input: #1F2937;
  --ring: #7C3AED;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-sans antialiased;
    font-family: var(--font-sans);
    overflow-x: hidden;
  }

  /* Smooth scrolling */
  html {
    scroll-behavior: smooth;
  }

  /* Selection styles */
  ::selection {
    background: rgba(124, 58, 237, 0.3);
    color: var(--foreground);
  }

  /* Focus styles */
  *:focus-visible {
    outline: 2px solid var(--primary);
    outline-offset: 2px;
  }
}

@layer components {
  /* Glass morphism surfaces */
  .glass {
    background: var(--glass);
    backdrop-filter: blur(24px);
    border: 1px solid var(--glass-border);
  }

  .glass-card {
    @apply glass rounded-2xl shadow-2xl;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3), 
                0 0 0 1px rgba(255, 255, 255, 0.05);
  }

  /* Premium card styles */
  .card-premium {
    @apply glass-card transition-all duration-500 hover:shadow-2xl;
    background: linear-gradient(135deg, var(--glass) 0%, rgba(124, 58, 237, 0.08) 100%);
    position: relative;
    overflow: hidden;
  }

  .card-premium::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    transition: left 0.6s ease;
  }

  .card-premium:hover::before {
    left: 100%;
  }

  .card-premium:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5),
                0 0 0 1px rgba(124, 58, 237, 0.3),
                0 0 80px rgba(124, 58, 237, 0.1);
  }

  /* Mega premium card for hero sections */
  .card-mega {
    @apply card-premium;
    background: linear-gradient(135deg, 
      rgba(124, 58, 237, 0.1) 0%, 
      rgba(34, 211, 238, 0.1) 50%, 
      rgba(16, 185, 129, 0.1) 100%);
    border: 2px solid transparent;
    background-clip: padding-box;
  }

  .card-mega::after {
    content: '';
    position: absolute;
    inset: 0;
    padding: 2px;
    background: linear-gradient(135deg, #7C3AED, #22D3EE, #10B981);
    border-radius: inherit;
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask-composite: exclude;
    z-index: -1;
    animation: gradient 6s ease infinite;
  }

  /* Typography */
  .heading-display {
    font-family: var(--font-display);
    letter-spacing: -0.025em;
    font-weight: 700;
  }

  .body-text {
    font-family: var(--font-sans);
    opacity: 0.9;
  }

  /* Status indicators */
  .status-dot {
    @apply w-2 h-2 rounded-full animate-pulse;
  }

  .status-online {
    @apply bg-green-500;
  }

  .status-warning {
    @apply bg-yellow-500;
  }

  .status-offline {
    @apply bg-red-500;
  }

  /* Interactive elements */
  .btn-glass {
    @apply glass rounded-xl px-6 py-3 font-medium transition-all duration-300;
    background: linear-gradient(135deg, var(--glass) 0%, rgba(124, 58, 237, 0.1) 100%);
    position: relative;
    overflow: hidden;
  }

  .btn-glass::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: left 0.5s ease;
  }

  .btn-glass:hover::before {
    left: 100%;
  }

  .btn-glass:hover {
    background: linear-gradient(135deg, rgba(124, 58, 237, 0.2) 0%, rgba(124, 58, 237, 0.3) 100%);
    box-shadow: 0 0 30px rgba(124, 58, 237, 0.4), 0 5px 20px rgba(0, 0, 0, 0.3);
    transform: translateY(-2px);
  }

  /* Premium button styles */
  .btn-premium {
    @apply relative overflow-hidden rounded-xl px-8 py-4 font-medium transition-all duration-300;
    background: linear-gradient(135deg, #7C3AED 0%, #22D3EE 100%);
    box-shadow: 0 4px 20px rgba(124, 58, 237, 0.3);
  }

  .btn-premium::before {
    content: '';
    position: absolute;
    inset: 0;
    background: linear-gradient(135deg, #22D3EE 0%, #10B981 100%);
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  .btn-premium:hover::before {
    opacity: 1;
  }

  .btn-premium:hover {
    transform: translateY(-3px) scale(1.05);
    box-shadow: 0 10px 40px rgba(124, 58, 237, 0.4), 0 0 60px rgba(34, 211, 238, 0.2);
  }

  .btn-premium span {
    position: relative;
    z-index: 1;
  }

  /* Utility gradients */
  .gradient-primary {
    background: linear-gradient(135deg, #7C3AED 0%, #22D3EE 100%);
  }

  .gradient-text {
    background: linear-gradient(135deg, #7C3AED 0%, #22D3EE 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  /* Advanced background effects */
  .noise-bg {
    position: relative;
  }

  .noise-bg::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    opacity: 0.03;
    background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.65' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)'/%3E%3C/svg%3E");
    pointer-events: none;
  }

  /* Hero gradient background */
  .hero-bg {
    background: linear-gradient(135deg, 
      var(--background) 0%, 
      rgba(124, 58, 237, 0.05) 25%,
      rgba(34, 211, 238, 0.05) 50%,
      rgba(16, 185, 129, 0.05) 75%,
      var(--background) 100%);
    background-size: 400% 400%;
    animation: gradient 15s ease infinite;
  }

  /* Floating orbs */
  .floating-orb {
    position: absolute;
    border-radius: 50%;
    filter: blur(40px);
    opacity: 0.3;
    animation: levitate 8s ease-in-out infinite;
  }

  .floating-orb:nth-child(1) {
    width: 200px;
    height: 200px;
    background: linear-gradient(135deg, #7C3AED, #22D3EE);
    top: 10%;
    left: 10%;
    animation-delay: 0s;
  }

  .floating-orb:nth-child(2) {
    width: 150px;
    height: 150px;
    background: linear-gradient(135deg, #22D3EE, #10B981);
    top: 60%;
    right: 10%;
    animation-delay: -4s;
  }

  .floating-orb:nth-child(3) {
    width: 100px;
    height: 100px;
    background: linear-gradient(135deg, #10B981, #F59E0B);
    bottom: 20%;
    left: 30%;
    animation-delay: -2s;
  }

  /* Mesh gradient overlay */
  .mesh-gradient {
    background: 
      radial-gradient(circle at 20% 20%, rgba(124, 58, 237, 0.3) 0%, transparent 50%),
      radial-gradient(circle at 80% 80%, rgba(34, 211, 238, 0.3) 0%, transparent 50%),
      radial-gradient(circle at 40% 40%, rgba(16, 185, 129, 0.2) 0%, transparent 50%);
    background-size: 100% 100%, 100% 100%, 100% 100%;
    background-attachment: fixed;
  }

  /* Shimmer effect */
  .shimmer {
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    background-size: 200px 100%;
    background-repeat: no-repeat;
    background-position: -200px 0;
  }

  .shimmer:hover {
    animation: shimmer 1.5s ease-in-out;
  }
}

@layer utilities {
  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-pulse-slow {
    animation: pulse 3s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  .animate-shimmer {
    animation: shimmer 2s linear infinite;
  }

  .animate-gradient {
    animation: gradient 15s ease infinite;
  }

  .animate-levitate {
    animation: levitate 4s ease-in-out infinite;
  }

  .animate-rotate-slow {
    animation: rotate-slow 20s linear infinite;
  }

  .animate-slide-up {
    animation: slide-up 0.6s cubic-bezier(0.16, 1, 0.3, 1);
  }

  .animate-scale-in {
    animation: scale-in 0.4s cubic-bezier(0.16, 1, 0.3, 1);
  }
}

@keyframes glow {
  from {
    box-shadow: 0 0 20px -10px var(--primary);
  }
  to {
    box-shadow: 0 0 20px -5px var(--primary);
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-20px);
  }
}

@keyframes shimmer {
  0% {
    background-position: -200px 0;
  }
  100% {
    background-position: calc(200px + 100%) 0;
  }
}

@keyframes gradient {
  0%, 100% {
    background-size: 400% 400%;
    background-position: 0% 50%;
  }
  50% {
    background-size: 400% 400%;
    background-position: 100% 50%;
  }
}

@keyframes levitate {
  0%, 100% {
    transform: translateY(0) rotate(0deg);
  }
  50% {
    transform: translateY(-10px) rotate(1deg);
  }
}

@keyframes rotate-slow {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@keyframes slide-up {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes scale-in {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: var(--muted);
}

::-webkit-scrollbar-thumb {
  background: var(--cyber-blue);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--cyber-purple);
}

/* Loading spinner for buttons */
.btn-loading {
  position: relative;
  color: transparent;
}

.btn-loading::after {
  content: "";
  position: absolute;
  width: 16px;
  height: 16px;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  margin: auto;
  border: 2px solid transparent;
  border-top-color: var(--primary-foreground);
  border-radius: 50%;
  animation: spin 1s ease infinite;
}

@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

/* Form focus states */
.form-focus {
  transition: all 0.3s ease;
}

.form-focus:focus {
  border-color: var(--cyber-blue);
  box-shadow: 0 0 0 2px hsla(196, 100%, 50%, 0.2);
  transform: scale(1.02);
}

/* Card hover effects */
.card-hover {
  transition: all 0.5s ease;
}

.card-hover:hover {
  transform: scale(1.05);
  box-shadow: 0 25px 50px -12px hsla(196, 100%, 50%, 0.25);
}

/* Gradient text */
.gradient-text {
  background: linear-gradient(45deg, var(--cyber-blue), var(--cyber-purple));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* Dot pattern background */
.dot-pattern {
  background-image: radial-gradient(circle, var(--cyber-blue) 1px, transparent 1px);
  background-size: 60px 60px;
  opacity: 0.1;
}

