import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient, useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { z } from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { AlertCircle, Sparkles, TrendingUp, Target, Plus, X } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

const campaignSchema = z.object({
  title: z.string().min(5, "Title must be at least 5 characters"),
  description: z.string().min(50, "Description must be at least 50 characters"),
  category: z.string().min(1, "Please select a category"),
  fundingType: z.enum(["donation", "reward", "equity"], {
    errorMap: () => ({ message: "Please select a funding type" }),
  }),
  goalAmount: z.string().min(1, "Goal amount is required"),
  currency: z.string().default("ETH"),
  deadline: z.string().min(1, "Deadline is required"),
  imageUrl: z.string().url().optional().or(z.literal("")),
  tags: z.array(z.string()).max(10, "Maximum 10 tags allowed"),
});

type CampaignFormData = z.infer<typeof campaignSchema>;

interface RewardTier {
  tier: string;
  amount: string;
  description: string;
  estimatedDelivery?: string;
  limit?: number;
}

export default function CreateCampaign() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [, setLocation] = useLocation();
  const [currentTag, setCurrentTag] = useState("");
  const [tags, setTags] = useState<string[]>([]);
  const [rewardTiers, setRewardTiers] = useState<RewardTier[]>([]);
  const [aiSuggestions, setAiSuggestions] = useState<{
    title?: string[];
    description?: string;
    credibilityScore?: number;
    fundingPrediction?: any;
  }>({});

  const form = useForm<CampaignFormData>({
    resolver: zodResolver(campaignSchema),
    defaultValues: {
      title: "",
      description: "",
      category: "",
      fundingType: "reward",
      goalAmount: "",
      currency: "ETH",
      deadline: "",
      imageUrl: "",
      tags: [],
    },
  });

  // AI Title Optimization
  const titleOptimization = useMutation({
    mutationFn: async (title: string) => {
      return apiRequest("POST", "/api/ai/optimize-title", { title });
    },
    onSuccess: (data) => {
      setAiSuggestions(prev => ({ ...prev, title: data.suggestions }));
    },
  });

  // AI Description Enhancement
  const descriptionEnhancement = useMutation({
    mutationFn: async (description: string, category: string) => {
      return apiRequest("POST", "/api/ai/enhance-description", { description, category });
    },
    onSuccess: (data) => {
      setAiSuggestions(prev => ({ ...prev, description: data.enhanced }));
    },
  });

  // AI Credibility Analysis
  const credibilityAnalysis = useMutation({
    mutationFn: async (campaignData: any) => {
      return apiRequest("POST", "/api/ai/analyze-credibility", { campaignData });
    },
    onSuccess: (data) => {
      setAiSuggestions(prev => ({ ...prev, credibilityScore: data.score }));
    },
  });

  // AI Funding Prediction
  const fundingPrediction = useMutation({
    mutationFn: async (campaignData: any) => {
      return apiRequest("POST", "/api/ai/predict-funding", { campaignData });
    },
    onSuccess: (data) => {
      setAiSuggestions(prev => ({ ...prev, fundingPrediction: data }));
    },
  });

  const createCampaignMutation = useMutation({
    mutationFn: async (data: CampaignFormData & { rewards?: RewardTier[] }) => {
      return apiRequest("POST", "/api/campaigns", {
        ...data,
        tags: tags,
        rewards: rewardTiers.length > 0 ? rewardTiers : undefined,
      });
    },
    onSuccess: (data) => {
      toast({
        title: "Campaign Created Successfully!",
        description: "Your campaign is now live and accepting contributions.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns"] });
      setLocation(`/campaigns/${data.id}`);
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to Create Campaign",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const addTag = () => {
    if (currentTag && !tags.includes(currentTag) && tags.length < 10) {
      setTags([...tags, currentTag]);
      setCurrentTag("");
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  const addRewardTier = () => {
    setRewardTiers([...rewardTiers, {
      tier: `Tier ${rewardTiers.length + 1}`,
      amount: "",
      description: "",
    }]);
  };

  const removeRewardTier = (index: number) => {
    setRewardTiers(rewardTiers.filter((_, i) => i !== index));
  };

  const updateRewardTier = (index: number, field: keyof RewardTier, value: string | number) => {
    setRewardTiers(prev => prev.map((tier, i) => 
      i === index ? { ...tier, [field]: value } : tier
    ));
  };

  // Check if user is approved for KYC
  if (!user) {
    return <div className="container mx-auto px-4 py-8">Loading...</div>;
  }

  if (user.kycStatus !== "approved") {
    return (
      <div className="container mx-auto px-4 py-8">
        <Alert className="max-w-2xl mx-auto">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>
            You need to complete KYC verification before creating campaigns.{" "}
            <a href="/kyc" className="text-blue-600 hover:underline">
              Complete verification here
            </a>
          </AlertDescription>
        </Alert>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8" data-testid="create-campaign-page">
      <div className="max-w-4xl mx-auto">
        <Card className="glass-morphism border-cyan-200 dark:border-cyan-800">
          <CardHeader className="text-center">
            <CardTitle className="text-3xl bg-gradient-to-r from-cyan-400 to-purple-600 bg-clip-text text-transparent">
              Create New Campaign
            </CardTitle>
            <CardDescription>
              Launch your project with blockchain transparency and AI-powered optimization
            </CardDescription>
          </CardHeader>

          <CardContent>
            <form onSubmit={form.handleSubmit((data) => createCampaignMutation.mutate({ ...data, rewards: rewardTiers }))} className="space-y-8">
              {/* Campaign Title with AI Optimization */}
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <Label htmlFor="title">Campaign Title *</Label>
                  <Button
                    type="button"
                    variant="outline"
                    size="sm"
                    onClick={() => titleOptimization.mutate(form.watch("title"))}
                    disabled={!form.watch("title") || titleOptimization.isPending}
                    data-testid="button-optimize-title"
                  >
                    <Sparkles className="w-4 h-4 mr-2" />
                    {titleOptimization.isPending ? "Optimizing..." : "AI Optimize"}
                  </Button>
                </div>

                <Input
                  id="title"
                  {...form.register("title")}
                  placeholder="Enter your campaign title"
                  className="text-lg"
                  data-testid="input-campaign-title"
                />

                {form.formState.errors.title && (
                  <p className="text-red-500 text-sm">{form.formState.errors.title.message}</p>
                )}

                {/* AI Title Suggestions */}
                {aiSuggestions.title && aiSuggestions.title.length > 0 && (
                  <div className="mt-4 p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
                    <h4 className="font-medium text-blue-900 dark:text-blue-100 mb-2">
                      ✨ AI-Optimized Title Suggestions:
                    </h4>
                    <div className="space-y-2">
                      {aiSuggestions.title.map((suggestion, index) => (
                        <button
                          key={index}
                          type="button"
                          onClick={() => form.setValue("title", suggestion)}
                          className="block w-full text-left p-2 hover:bg-blue-100 dark:hover:bg-blue-800 rounded text-sm"
                          data-testid={`button-title-suggestion-${index}`}
                        >
                          {suggestion}
                        </button>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              {/* Campaign Description with AI Enhancement */}
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <Label htmlFor="description">Campaign Description *</Label>
                  <Button
                    type="button"
                    variant="outline"
                    size="sm"
                    onClick={() => descriptionEnhancement.mutate(form.watch("description"), form.watch("category"))}
                    disabled={!form.watch("description") || !form.watch("category") || descriptionEnhancement.isPending}
                    data-testid="button-enhance-description"
                  >
                    <TrendingUp className="w-4 h-4 mr-2" />
                    {descriptionEnhancement.isPending ? "Enhancing..." : "AI Enhance"}
                  </Button>
                </div>

                <Textarea
                  id="description"
                  {...form.register("description")}
                  placeholder="Describe your project, goals, and why people should support it..."
                  rows={6}
                  data-testid="textarea-campaign-description"
                />

                {form.formState.errors.description && (
                  <p className="text-red-500 text-sm">{form.formState.errors.description.message}</p>
                )}

                {/* AI Enhanced Description */}
                {aiSuggestions.description && (
                  <div className="mt-4 p-4 bg-green-50 dark:bg-green-900/20 rounded-lg">
                    <h4 className="font-medium text-green-900 dark:text-green-100 mb-2">
                      ✨ AI-Enhanced Description:
                    </h4>
                    <div className="prose dark:prose-invert text-sm max-w-none">
                      <p className="whitespace-pre-wrap">{aiSuggestions.description}</p>
                    </div>
                    <Button
                      type="button"
                      variant="outline"
                      size="sm"
                      onClick={() => form.setValue("description", aiSuggestions.description!)}
                      className="mt-3"
                      data-testid="button-use-enhanced-description"
                    >
                      Use Enhanced Version
                    </Button>
                  </div>
                )}
              </div>

              {/* Category and Funding Type */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="space-y-2">
                  <Label htmlFor="category">Category *</Label>
                  <Select 
                    value={form.watch("category")} 
                    onValueChange={(value) => form.setValue("category", value)}
                  >
                    <SelectTrigger data-testid="select-campaign-category">
                      <SelectValue placeholder="Select category" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="Technology">Technology</SelectItem>
                      <SelectItem value="Gaming">Gaming</SelectItem>
                      <SelectItem value="Art">Art & Design</SelectItem>
                      <SelectItem value="Film">Film & Video</SelectItem>
                      <SelectItem value="Music">Music</SelectItem>
                      <SelectItem value="Publishing">Publishing</SelectItem>
                      <SelectItem value="Food">Food</SelectItem>
                      <SelectItem value="Fashion">Fashion</SelectItem>
                      <SelectItem value="Health">Health</SelectItem>
                      <SelectItem value="Education">Education</SelectItem>
                      <SelectItem value="Environment">Environment</SelectItem>
                      <SelectItem value="Community">Community</SelectItem>
                    </SelectContent>
                  </Select>
                  {form.formState.errors.category && (
                    <p className="text-red-500 text-sm">{form.formState.errors.category.message}</p>
                  )}
                </div>

                <div className="space-y-2">
                  <Label htmlFor="funding-type">Funding Type *</Label>
                  <Select 
                    value={form.watch("fundingType")} 
                    onValueChange={(value) => form.setValue("fundingType", value as "donation" | "reward" | "equity")}
                  >
                    <SelectTrigger data-testid="select-funding-type">
                      <SelectValue placeholder="Select funding type" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="donation">Donation-based</SelectItem>
                      <SelectItem value="reward">Reward-based</SelectItem>
                      <SelectItem value="equity">Equity-based</SelectItem>
                    </SelectContent>
                  </Select>
                  {form.formState.errors.fundingType && (
                    <p className="text-red-500 text-sm">{form.formState.errors.fundingType.message}</p>
                  )}
                </div>
              </div>

              {/* Funding Goal and Deadline */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div className="space-y-2">
                  <Label htmlFor="goal-amount">Funding Goal *</Label>
                  <Input
                    id="goal-amount"
                    type="number"
                    step="0.001"
                    {...form.register("goalAmount")}
                    placeholder="100"
                    data-testid="input-goal-amount"
                  />
                  {form.formState.errors.goalAmount && (
                    <p className="text-red-500 text-sm">{form.formState.errors.goalAmount.message}</p>
                  )}
                </div>

                <div className="space-y-2">
                  <Label htmlFor="currency">Currency</Label>
                  <Select 
                    value={form.watch("currency")} 
                    onValueChange={(value) => form.setValue("currency", value)}
                  >
                    <SelectTrigger data-testid="select-currency">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="ETH">ETH (Ethereum)</SelectItem>
                      <SelectItem value="MATIC">MATIC (Polygon)</SelectItem>
                      <SelectItem value="USDC">USDC (Stable)</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="deadline">Campaign Deadline *</Label>
                  <Input
                    id="deadline"
                    type="date"
                    {...form.register("deadline")}
                    min={new Date().toISOString().split('T')[0]}
                    data-testid="input-campaign-deadline"
                  />
                  {form.formState.errors.deadline && (
                    <p className="text-red-500 text-sm">{form.formState.errors.deadline.message}</p>
                  )}
                </div>
              </div>

              {/* Campaign Image */}
              <div className="space-y-2">
                <Label htmlFor="image-url">Campaign Image URL (Optional)</Label>
                <Input
                  id="image-url"
                  type="url"
                  {...form.register("imageUrl")}
                  placeholder="https://example.com/your-image.jpg"
                  data-testid="input-image-url"
                />
                {form.formState.errors.imageUrl && (
                  <p className="text-red-500 text-sm">{form.formState.errors.imageUrl.message}</p>
                )}
              </div>

              {/* Tags */}
              <div className="space-y-4">
                <Label>Tags (Optional, max 10)</Label>
                <div className="flex gap-2">
                  <Input
                    value={currentTag}
                    onChange={(e) => setCurrentTag(e.target.value)}
                    placeholder="Add a tag..."
                    onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addTag())}
                    data-testid="input-campaign-tag"
                  />
                  <Button
                    type="button"
                    variant="outline"
                    onClick={addTag}
                    disabled={!currentTag || tags.length >= 10}
                    data-testid="button-add-tag"
                  >
                    <Plus className="w-4 h-4" />
                  </Button>
                </div>

                {/* Display Tags */}
                {tags.length > 0 && (
                  <div className="flex flex-wrap gap-2">
                    {tags.map((tag, index) => (
                      <Badge key={index} variant="secondary" className="flex items-center gap-1">
                        {tag}
                        <X 
                          className="w-3 h-3 cursor-pointer" 
                          onClick={() => removeTag(tag)}
                          data-testid={`button-remove-tag-${index}`}
                        />
                      </Badge>
                    ))}
                  </div>
                )}
              </div>

              {/* Reward Tiers (for reward-based campaigns) */}
              {form.watch("fundingType") === "reward" && (
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <Label>Reward Tiers</Label>
                    <Button
                      type="button"
                      variant="outline"
                      onClick={addRewardTier}
                      data-testid="button-add-reward-tier"
                    >
                      <Plus className="w-4 h-4 mr-2" />
                      Add Reward Tier
                    </Button>
                  </div>

                  {rewardTiers.map((tier, index) => (
                    <Card key={index} className="p-4">
                      <div className="flex justify-between items-center mb-4">
                        <h4 className="font-medium">Reward Tier {index + 1}</h4>
                        <Button
                          type="button"
                          variant="ghost"
                          size="sm"
                          onClick={() => removeRewardTier(index)}
                          data-testid={`button-remove-reward-tier-${index}`}
                        >
                          <X className="w-4 h-4" />
                        </Button>
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <Input
                          placeholder="Tier name"
                          value={tier.tier}
                          onChange={(e) => updateRewardTier(index, "tier", e.target.value)}
                          data-testid={`input-tier-name-${index}`}
                        />
                        <Input
                          placeholder="Minimum amount"
                          value={tier.amount}
                          onChange={(e) => updateRewardTier(index, "amount", e.target.value)}
                          data-testid={`input-tier-amount-${index}`}
                        />
                      </div>

                      <Textarea
                        placeholder="Reward description"
                        value={tier.description}
                        onChange={(e) => updateRewardTier(index, "description", e.target.value)}
                        className="mt-4"
                        data-testid={`textarea-tier-description-${index}`}
                      />
                    </Card>
                  ))}
                </div>
              )}

              {/* AI Analysis Section */}
              <div className="space-y-4">
                <Separator />
                <div className="flex items-center gap-4">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => credibilityAnalysis.mutate({
                      title: form.watch("title"),
                      description: form.watch("description"),
                      category: form.watch("category"),
                      goalAmount: form.watch("goalAmount"),
                    })}
                    disabled={credibilityAnalysis.isPending}
                    data-testid="button-analyze-credibility"
                  >
                    <Target className="w-4 h-4 mr-2" />
                    {credibilityAnalysis.isPending ? "Analyzing..." : "Analyze Credibility"}
                  </Button>

                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => fundingPrediction.mutate({
                      title: form.watch("title"),
                      description: form.watch("description"),
                      category: form.watch("category"),
                      goalAmount: form.watch("goalAmount"),
                      tags: tags,
                    })}
                    disabled={fundingPrediction.isPending}
                    data-testid="button-predict-funding"
                  >
                    <TrendingUp className="w-4 h-4 mr-2" />
                    {fundingPrediction.isPending ? "Predicting..." : "Predict Success"}
                  </Button>
                </div>

                {/* AI Analysis Results */}
                {(aiSuggestions.credibilityScore || aiSuggestions.fundingPrediction) && (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {aiSuggestions.credibilityScore && (
                      <Card className="p-4 bg-yellow-50 dark:bg-yellow-900/20">
                        <h4 className="font-medium text-yellow-800 dark:text-yellow-200">
                          Credibility Score: {aiSuggestions.credibilityScore}/10
                        </h4>
                        <p className="text-sm text-yellow-700 dark:text-yellow-300 mt-1">
                          Based on campaign details and creator profile
                        </p>
                      </Card>
                    )}

                    {aiSuggestions.fundingPrediction && (
                      <Card className="p-4 bg-purple-50 dark:bg-purple-900/20">
                        <h4 className="font-medium text-purple-800 dark:text-purple-200">
                          Success Probability: {Math.round(aiSuggestions.fundingPrediction.probability * 100)}%
                        </h4>
                        <p className="text-sm text-purple-700 dark:text-purple-300 mt-1">
                          {aiSuggestions.fundingPrediction.recommendation}
                        </p>
                      </Card>
                    )}
                  </div>
                )}
              </div>

              {/* Submit Button */}
              <Button 
                type="submit" 
                className="w-full h-12 text-lg"
                disabled={createCampaignMutation.isPending}
                data-testid="button-create-campaign"
              >
                {createCampaignMutation.isPending ? "Creating Campaign..." : "Create Campaign"}
              </Button>
            </form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/KYCVerification.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { z } from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Upload, CheckCircle, AlertCircle, FileText } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

const kycSchema = z.object({
  documentType: z.string().min(1, "Document type is required"),
  fullName: z.string().min(2, "Full name must be at least 2 characters"),
  dateOfBirth: z.string().min(1, "Date of birth is required"),
  nationality: z.string().min(1, "Nationality is required"),
  address: z.string().min(10, "Please provide a complete address"),
  phoneNumber: z.string().min(10, "Please provide a valid phone number"),
  occupation: z.string().min(2, "Occupation is required"),
  sourceOfFunds: z.string().min(10, "Please explain your source of funds"),
});

type KycFormData = z.infer<typeof kycSchema>;

export default function KYCVerification() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [uploadedFiles, setUploadedFiles] = useState<File[]>([]);

  const form = useForm<KycFormData>({
    resolver: zodResolver(kycSchema),
    defaultValues: {
      documentType: "",
      fullName: "",
      dateOfBirth: "",
      nationality: "",
      address: "",
      phoneNumber: "",
      occupation: "",
      sourceOfFunds: "",
    },
  });

  const kycMutation = useMutation({
    mutationFn: async (data: KycFormData) => {
      // Convert files to base64 for storage
      const filePromises = uploadedFiles.map(file => {
        return new Promise<{ name: string; type: string; data: string }>((resolve, reject) => {
          const reader = new FileReader();
          reader.onload = () => resolve({
            name: file.name,
            type: file.type,
            data: reader.result as string,
          });
          reader.onerror = reject;
          reader.readAsDataURL(file);
        });
      });

      const files = await Promise.all(filePromises);

      return apiRequest("PUT", "/api/user/kyc", {
        documents: {
          ...data,
          uploadedFiles: files,
          submissionDate: new Date().toISOString(),
        },
        status: "pending"
      });
    },
    onSuccess: () => {
      toast({
        title: "KYC Submitted Successfully",
        description: "Your documents are being reviewed. You'll receive an update within 2-3 business days.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/user"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Submission Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(event.target.files || []);
    const validFiles = files.filter(file => {
      const isValidType = ['image/jpeg', 'image/png', 'application/pdf'].includes(file.type);
      const isValidSize = file.size <= 10 * 1024 * 1024; // 10MB limit
      return isValidType && isValidSize;
    });

    if (validFiles.length !== files.length) {
      toast({
        title: "Invalid Files",
        description: "Please upload only JPG, PNG, or PDF files under 10MB each.",
        variant: "destructive",
      });
    }

    setUploadedFiles(prev => [...prev, ...validFiles]);
  };

  const removeFile = (index: number) => {
    setUploadedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const getKycStatusColor = (status: string) => {
    switch (status) {
      case "approved": return "text-green-600 bg-green-50";
      case "rejected": return "text-red-600 bg-red-50";
      default: return "text-yellow-600 bg-yellow-50";
    }
  };

  if (!user) {
    return (
      <div className="container mx-auto px-4 py-8" data-testid="kyc-loading">
        <div className="text-center">Loading...</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8" data-testid="kyc-verification-page">
      <div className="max-w-2xl mx-auto">
        <Card className="glass-morphism border-cyan-200 dark:border-cyan-800">
          <CardHeader className="text-center">
            <CardTitle className="text-3xl bg-gradient-to-r from-cyan-400 to-purple-600 bg-clip-text text-transparent">
              KYC Verification
            </CardTitle>
            <CardDescription>
              Complete your identity verification to create and manage campaigns
            </CardDescription>
          </CardHeader>

          <CardContent>
            {/* Current Status */}
            <Alert className={`mb-6 ${getKycStatusColor(user.kycStatus || "pending")}`}>
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>
                <strong>Current Status: </strong>
                {user.kycStatus === "approved" && "✅ Verified - You can create campaigns"}
                {user.kycStatus === "rejected" && "❌ Rejected - Please resubmit with correct documents"}
                {user.kycStatus === "pending" && "⏳ Pending Review - Documents submitted and under review"}
                {!user.kycStatus && "📋 Not Started - Please complete verification below"}
              </AlertDescription>
            </Alert>

            {(user.kycStatus !== "approved") && (
              <form onSubmit={form.handleSubmit((data) => kycMutation.mutate(data))} className="space-y-6">
                {/* Document Type */}
                <div className="space-y-2">
                  <Label htmlFor="document-type">Document Type *</Label>
                  <Select 
                    value={form.watch("documentType")} 
                    onValueChange={(value) => form.setValue("documentType", value)}
                  >
                    <SelectTrigger data-testid="select-document-type">
                      <SelectValue placeholder="Select document type" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="passport">Passport</SelectItem>
                      <SelectItem value="driver_license">Driver's License</SelectItem>
                      <SelectItem value="national_id">National ID Card</SelectItem>
                    </SelectContent>
                  </Select>
                  {form.formState.errors.documentType && (
                    <p className="text-red-500 text-sm">{form.formState.errors.documentType.message}</p>
                  )}
                </div>

                {/* Personal Information */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="full-name">Full Name *</Label>
                    <Input 
                      id="full-name"
                      {...form.register("fullName")}
                      placeholder="As shown on your ID"
                      data-testid="input-full-name"
                    />
                    {form.formState.errors.fullName && (
                      <p className="text-red-500 text-sm">{form.formState.errors.fullName.message}</p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="date-of-birth">Date of Birth *</Label>
                    <Input 
                      id="date-of-birth"
                      type="date"
                      {...form.register("dateOfBirth")}
                      data-testid="input-date-of-birth"
                    />
                    {form.formState.errors.dateOfBirth && (
                      <p className="text-red-500 text-sm">{form.formState.errors.dateOfBirth.message}</p>
                    )}
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="nationality">Nationality *</Label>
                    <Input 
                      id="nationality"
                      {...form.register("nationality")}
                      placeholder="e.g., United States"
                      data-testid="input-nationality"
                    />
                    {form.formState.errors.nationality && (
                      <p className="text-red-500 text-sm">{form.formState.errors.nationality.message}</p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="phone">Phone Number *</Label>
                    <Input 
                      id="phone"
                      {...form.register("phoneNumber")}
                      placeholder="+1 (555) 123-4567"
                      data-testid="input-phone-number"
                    />
                    {form.formState.errors.phoneNumber && (
                      <p className="text-red-500 text-sm">{form.formState.errors.phoneNumber.message}</p>
                    )}
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="address">Complete Address *</Label>
                  <Textarea 
                    id="address"
                    {...form.register("address")}
                    placeholder="Street address, city, state/province, postal code, country"
                    data-testid="textarea-address"
                  />
                  {form.formState.errors.address && (
                    <p className="text-red-500 text-sm">{form.formState.errors.address.message}</p>
                  )}
                </div>

                <div className="space-y-2">
                  <Label htmlFor="occupation">Occupation *</Label>
                  <Input 
                    id="occupation"
                    {...form.register("occupation")}
                    placeholder="Your current job title or profession"
                    data-testid="input-occupation"
                  />
                  {form.formState.errors.occupation && (
                    <p className="text-red-500 text-sm">{form.formState.errors.occupation.message}</p>
                  )}
                </div>

                <div className="space-y-2">
                  <Label htmlFor="source-of-funds">Source of Funds *</Label>
                  <Textarea 
                    id="source-of-funds"
                    {...form.register("sourceOfFunds")}
                    placeholder="Explain where your funds come from (e.g., salary, savings, investments)"
                    data-testid="textarea-source-of-funds"
                  />
                  {form.formState.errors.sourceOfFunds && (
                    <p className="text-red-500 text-sm">{form.formState.errors.sourceOfFunds.message}</p>
                  )}
                </div>

                {/* File Upload */}
                <div className="space-y-4">
                  <Label>Upload Documents *</Label>
                  <div className="border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg p-6">
                    <div className="text-center">
                      <Upload className="mx-auto h-12 w-12 text-gray-400" />
                      <div className="mt-4">
                        <label htmlFor="file-upload" className="cursor-pointer">
                          <span className="mt-2 block text-sm font-medium text-gray-900 dark:text-gray-100">
                            Upload ID Documents
                          </span>
                          <input
                            id="file-upload"
                            type="file"
                            multiple
                            accept=".jpg,.jpeg,.png,.pdf"
                            onChange={handleFileUpload}
                            className="hidden"
                            data-testid="input-file-upload"
                          />
                        </label>
                        <p className="mt-1 text-xs text-gray-500">
                          JPG, PNG or PDF up to 10MB each
                        </p>
                      </div>
                    </div>
                  </div>

                  {/* Uploaded Files */}
                  {uploadedFiles.length > 0 && (
                    <div className="space-y-2">
                      {uploadedFiles.map((file, index) => (
                        <div key={index} className="flex items-center justify-between p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
                          <div className="flex items-center space-x-3">
                            <FileText className="h-5 w-5 text-blue-500" />
                            <span className="text-sm">{file.name}</span>
                            <span className="text-xs text-gray-500">
                              ({(file.size / 1024 / 1024).toFixed(1)} MB)
                            </span>
                          </div>
                          <Button
                            type="button"
                            variant="ghost"
                            size="sm"
                            onClick={() => removeFile(index)}
                            data-testid={`button-remove-file-${index}`}
                          >
                            Remove
                          </Button>
                        </div>
                      ))}
                    </div>
                  )}
                </div>

                {/* Submit Button */}
                <Button 
                  type="submit" 
                  className="w-full"
                  disabled={kycMutation.isPending || uploadedFiles.length === 0}
                  data-testid="button-submit-kyc"
                >
                  {kycMutation.isPending ? "Submitting..." : "Submit for Verification"}
                </Button>
              </form>
            )}

            {user.kycStatus === "approved" && (
              <div className="text-center py-8">
                <CheckCircle className="mx-auto h-16 w-16 text-green-500 mb-4" />
                <h3 className="text-xl font-semibold text-green-700 dark:text-green-400">
                  Verification Complete!
                </h3>
                <p className="text-gray-600 dark:text-gray-400 mt-2">
                  You can now create and manage campaigns on the platform.
                </p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/admin.tsx.content:
import { AdminDashboard } from "@/components/admin/AdminDashboard";
import { motion } from "framer-motion";
import { useAuth } from "@/hooks/useAuth";
import { useLocation } from "wouter";
import { useEffect, useState } from "react";
import { Shield, Lock } from "lucide-react";

export default function AdminPage() {
  const { user, isLoading } = useAuth();
  const [, setLocation] = useLocation();
  const [time, setTime] = useState(new Date());

  // Real-time clock
  useEffect(() => {
    const timer = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);



  // Show loading state first
  if (isLoading) {
    return (
      <div className="dark min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-blue-600 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-400">Loading...</p>
        </div>
      </div>
    );
  }

  // Show clean login prompt if not authenticated
  if (!user) {
    return (
      <div className="dark min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 flex items-center justify-center p-6">
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="w-full max-w-md"
        >
          <div className="bg-gray-800 rounded-lg shadow-lg p-8 border border-gray-700">
            <div className="text-center space-y-6">
              <div className="space-y-2">
                <div className="w-12 h-12 bg-blue-600 rounded-lg mx-auto flex items-center justify-center">
                  <Shield className="h-6 w-6 text-white" />
                </div>
                <h1 className="text-2xl font-bold text-white">
                  Admin Access
                </h1>
                <p className="text-gray-400">
                  Please log in to access the admin panel
                </p>
              </div>

              <button
                onClick={() => setLocation("/auth")}
                className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-3 px-4 rounded-lg transition-colors duration-200"
              >
                Sign In
              </button>

              <p className="text-xs text-gray-400">
                Secure authentication required
              </p>
            </div>
          </div>
        </motion.div>
      </div>
    );
  }

  // Show clean access denied if not admin
  if (user && user.role !== 'admin') {
    return (
      <div className="dark min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 flex items-center justify-center p-6">
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="w-full max-w-md"
        >
          <div className="bg-gray-800 rounded-lg shadow-lg p-8 border border-gray-700 text-center space-y-6">
            <div className="w-16 h-16 bg-red-900/30 rounded-full mx-auto flex items-center justify-center">
              <Lock className="h-8 w-8 text-red-400" />
            </div>

            <div className="space-y-2">
              <h1 className="text-2xl font-bold text-white">Access Denied</h1>
              <p className="text-gray-400">
                You don't have permission to access this area
              </p>
              <p className="text-sm text-gray-500">
                Current role: {user?.role || 'None'} • Required: Admin
              </p>
            </div>

            <button 
              onClick={() => setLocation("/")}
              className="w-full bg-gray-600 hover:bg-gray-700 text-white font-medium py-3 px-4 rounded-lg transition-colors duration-200"
            >
              Return to Home
            </button>
          </div>
        </motion.div>
      </div>
    );
  }

  return (
    <div className="dark min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900">
      {/* Glassmorphism Top Navbar */}
      <div className="sticky top-0 z-50 backdrop-blur-xl bg-gray-900/60 border-b border-gray-700/50 shadow-xl">
        <div className="px-6 py-4">
          <div className="flex items-center justify-between">
            {/* Logo with neon glow */}
            <div className="flex items-center space-x-4">
              <div className="w-10 h-10 bg-gradient-to-r from-blue-600 to-purple-600 rounded-lg flex items-center justify-center shadow-lg shadow-blue-500/25">
                <Shield className="h-6 w-6 text-white drop-shadow-sm" />
              </div>
              <div>
                <h1 className="text-xl font-bold text-white drop-shadow-sm">CryptoFund Admin</h1>
                <p className="text-sm text-gray-400">Welcome back, {user.username}</p>
              </div>
            </div>

            {/* Quick Search */}
            <div className="flex-1 max-w-md mx-8">
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <svg className="h-5 w-5 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                </div>
                <input
                  type="text"
                  className="block w-full pl-10 pr-3 py-2 border border-gray-600/50 rounded-lg bg-gray-800/50 backdrop-blur-sm text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500/50 focus:border-blue-500/50 transition-all duration-200"
                  placeholder="Search campaigns, users, payouts..."
                />
              </div>
            </div>

            {/* Profile & Time */}
            <div className="flex items-center space-x-4">
              <div className="text-sm text-gray-400 font-mono">
                {time.toLocaleTimeString()}
              </div>
              <div className="w-8 h-8 bg-gradient-to-r from-green-400 to-blue-500 rounded-full flex items-center justify-center shadow-lg shadow-green-400/25">
                <span className="text-xs font-bold text-white">{user.username.charAt(0).toUpperCase()}</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main dashboard content with glassmorphism */}
      <div className="p-6">
        <AdminDashboard />
      </div>
    </div>
  );
}

