                "linear-gradient(135deg, rgba(34, 211, 238, 0.1), rgba(16, 185, 129, 0.05), rgba(124, 58, 237, 0.1))",
                "linear-gradient(225deg, rgba(16, 185, 129, 0.1), rgba(124, 58, 237, 0.05), rgba(34, 211, 238, 0.1))"
