              >
                <motion.div
                  whileHover={{ rotate: 360 }}
                  transition={{ duration: 0.5 }}
                >
                  <Users className="w-4 h-4" />
                </motion.div>
                <span className="font-mono">{campaign.backerCount} backers</span>
