interface PageLayoutProps {
  children: React.ReactNode;
  showBackground?: boolean;
  showNavigation?: boolean;
  backTo?: string;
  backLabel?: string;
}

export function PageLayout({ 
  children, 
  showBackground = true, 
  showNavigation = true,
  backTo,
  backLabel
}: PageLayoutProps) {
