                    <AvatarFallback className="bg-gradient-to-br from-blue-500 to-purple-600 text-white">
                      {user.firstName?.[0] || user.email?.[0] || "U"}
                    </AvatarFallback>
                  </Avatar>
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent className="w-56" align="end" forceMount>
                <div className="flex flex-col space-y-1 p-2">
                  <p className="text-sm font-medium leading-none">
                    {user.firstName} {user.lastName}
                  </p>
                  <p className="text-xs leading-none text-muted-foreground">
                    {user.email}
                  </p>
                  <div className="flex items-center gap-2 mt-2">
                    <Badge variant={user.kycStatus === "approved" ? "default" : "secondary"}>
                      {user.kycStatus === "approved" ? (
                        <>
                          <Shield className="h-3 w-3 mr-1" />
                          Verified
                        </>
                      ) : (
                        "Pending KYC"
                      )}
                    </Badge>
                  </div>
                </div>
                <DropdownMenuSeparator />
                <DropdownMenuItem asChild>
                  <Link href="/dashboard" className="flex items-center">
                    <User className="mr-2 h-4 w-4" />
                    Dashboard
                  </Link>
                </DropdownMenuItem>
                <DropdownMenuItem asChild>
                  <Link href="/kyc" className="flex items-center">
                    <Shield className="mr-2 h-4 w-4" />
                    KYC Verification
                  </Link>
                </DropdownMenuItem>
                <DropdownMenuSeparator />
                <DropdownMenuItem onClick={logout} className="cursor-pointer">
                  <LogOut className="mr-2 h-4 w-4" />
                  Log out
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          ) : (
            <Button 
              onClick={() => window.location.href = '/auth'} 
              variant="outline"
              size="sm"
            >
              Login
            </Button>
          )}
        </div>
      </div>

      {/* Mobile Navigation */}
      <div className="md:hidden border-t">
        <div className="flex items-center justify-around py-2">
          {navItems.slice(0, 4).map((item) => {
            const isActive = location === item.href || (item.href !== "/" && location.startsWith(item.href));
            return (
              <Link key={item.href} href={item.href}>
                <Button
                  variant={isActive ? "default" : "ghost"}
                  size="sm"
                  className="flex flex-col items-center gap-1 h-auto py-2"
                >
                  <item.icon className="h-4 w-4" />
                  <span className="text-xs">{item.label}</span>
                </Button>
              </Link>
            );
          })}
        </div>
      </div>
    </nav>
  );
}

client/src/components/payment/CryptoPaym.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { 
  Wallet, 
  CreditCard, 
  ArrowRight, 
  CheckCircle, 
  AlertTriangle,
  ExternalLink 
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

interface CryptoPaymentProps {
  campaignId: string;
  onSuccess?: () => void;
}

const SUPPORTED_TOKENS = [
  { symbol: "ETH", name: "Ethereum", address: "0x0000000000000000000000000000000000000000" },
  { symbol: "MATIC", name: "Polygon", address: "0x0000000000000000000000000000000000000001" },
  { symbol: "USDC", name: "USD Coin", address: "0x2791bca1f2de4661ed88a30c99a7a9449aa84174" },
];

export function CryptoPayment({ campaignId, onSuccess }: CryptoPaymentProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [amount, setAmount] = useState("");
  const [selectedToken, setSelectedToken] = useState(SUPPORTED_TOKENS[0]);
  const [walletConnected, setWalletConnected] = useState(false);
  const [transactionStep, setTransactionStep] = useState<"input" | "confirm" | "processing" | "success">("input");

  const contributeMutation = useMutation({
    mutationFn: async ({ amount, currency, transactionHash }: { 
      amount: string; 
      currency: string; 
      transactionHash: string; 
    }) => {
      return apiRequest("POST", "/api/contributions", {
        campaignId,
        amount,
        currency,
        transactionHash,
        paymentMethod: "crypto",
        fromAddress: "0x1234567890123456789012345678901234567890", // Mock wallet address
        toAddress: "0x0987654321098765432109876543210987654321", // Mock campaign address
        gasUsed: "21000",
        gasPrice: "0.02",
        blockNumber: Math.floor(Math.random() * 1000000).toString(),
      });
    },
    onSuccess: () => {
      toast({
        title: "Contribution Successful!",
        description: "Your crypto contribution has been processed.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns", campaignId] });
      queryClient.invalidateQueries({ queryKey: ["/api/contributions"] });
      setTransactionStep("success");
      onSuccess?.();
    },
    onError: (error: any) => {
      toast({
        title: "Transaction Failed",
        description: error.message || "Failed to process crypto contribution.",
        variant: "destructive",
      });
      setTransactionStep("input");
    },
  });

  const connectWallet = async () => {
    // Mock wallet connection - in real app would use MetaMask or WalletConnect
    try {
      toast({
        title: "Wallet Connected",
        description: "Mock wallet connected successfully (0x1234...7890)",
      });
      setWalletConnected(true);
    } catch (error) {
      toast({
        title: "Connection Failed",
        description: "Please install MetaMask or use a Web3 browser.",
        variant: "destructive",
      });
    }
  };

  const handleContribute = async () => {
    if (!amount || parseFloat(amount) <= 0) {
      toast({
        title: "Invalid Amount",
        description: "Please enter a valid contribution amount.",
        variant: "destructive",
      });
      return;
    }

    setTransactionStep("confirm");
  };

  const confirmTransaction = async () => {
    setTransactionStep("processing");

    // Simulate blockchain transaction
    const mockTransactionHash = `0x${Math.random().toString(16).substr(2, 64)}`;

    // Simulate processing delay
    setTimeout(() => {
      contributeMutation.mutate({
        amount,
        currency: selectedToken.symbol,
        transactionHash: mockTransactionHash,
      });
    }, 2000);
  };

  const resetForm = () => {
    setAmount("");
    setTransactionStep("input");
  };

  if (transactionStep === "success") {
    return (
      <Card className="w-full max-w-md mx-auto">
        <CardContent className="p-6 text-center">
          <CheckCircle className="h-16 w-16 text-green-500 mx-auto mb-4" />
          <h3 className="text-lg font-semibold mb-2">Payment Successful!</h3>
          <p className="text-muted-foreground mb-4">
            Your contribution of {amount} {selectedToken.symbol} has been processed.
          </p>
          <div className="space-y-2">
            <Button onClick={resetForm} variant="outline" className="w-full">
              Make Another Contribution
            </Button>
            <Button asChild className="w-full">
              <a 
                href={`https://polygonscan.com/tx/0x${Math.random().toString(16).substr(2, 64)}`}
                target="_blank"
                rel="noopener noreferrer"
                className="flex items-center gap-2"
              >
                View on Explorer
                <ExternalLink className="h-4 w-4" />
              </a>
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Wallet className="h-5 w-5" />
          Crypto Payment
        </CardTitle>
        <CardDescription>
          Support this campaign with cryptocurrency
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        {!walletConnected ? (
          <div className="text-center space-y-4">
            <div className="bg-muted/50 rounded-lg p-4">
              <AlertTriangle className="h-8 w-8 text-amber-500 mx-auto mb-2" />
              <p className="text-sm text-muted-foreground">
                Connect your crypto wallet to contribute
              </p>
            </div>
            <Button onClick={connectWallet} className="w-full">
              <Wallet className="h-4 w-4 mr-2" />
              Connect Wallet
            </Button>
          </div>
        ) : (
          <>
            {/* Wallet Status */}
            <div className="bg-green-50 dark:bg-green-950 rounded-lg p-3">
              <div className="flex items-center gap-2">
                <CheckCircle className="h-4 w-4 text-green-600" />
                <span className="text-sm font-medium text-green-700 dark:text-green-300">
                  Wallet Connected
                </span>
              </div>
              <p className="text-xs text-green-600 dark:text-green-400 mt-1">
                0x1234...7890 (Mock Wallet)
              </p>
            </div>

            {transactionStep === "input" && (
              <>
                {/* Token Selection */}
                <div className="space-y-2">
                  <Label>Payment Token</Label>
                  <Select 
                    value={selectedToken.symbol} 
                    onValueChange={(value) => {
                      const token = SUPPORTED_TOKENS.find(t => t.symbol === value);
                      if (token) setSelectedToken(token);
                    }}
                  >
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {SUPPORTED_TOKENS.map((token) => (
                        <SelectItem key={token.symbol} value={token.symbol}>
                          <div className="flex items-center gap-2">
                            <span className="font-medium">{token.symbol}</span>
                            <span className="text-muted-foreground">- {token.name}</span>
                          </div>
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* Amount Input */}
                <div className="space-y-2">
                  <Label htmlFor="amount">Contribution Amount</Label>
                  <div className="relative">
                    <Input
                      id="amount"
                      type="number"
                      placeholder="0.00"
                      value={amount}
                      onChange={(e) => setAmount(e.target.value)}
                      className="pr-16"
                    />
                    <Badge variant="secondary" className="absolute right-2 top-1/2 -translate-y-1/2">
                      {selectedToken.symbol}
                    </Badge>
                  </div>
                </div>

                {/* Quick Amount Buttons */}
                <div className="grid grid-cols-3 gap-2">
                  {["0.1", "0.5", "1.0"].map((quickAmount) => (
                    <Button
                      key={quickAmount}
                      variant="outline"
                      size="sm"
                      onClick={() => setAmount(quickAmount)}
                    >
                      {quickAmount} {selectedToken.symbol}
                    </Button>
                  ))}
                </div>

                <Button onClick={handleContribute} className="w-full" disabled={!amount}>
                  Continue
                  <ArrowRight className="h-4 w-4 ml-2" />
                </Button>
              </>
            )}

            {transactionStep === "confirm" && (
              <div className="space-y-4">
                <div className="bg-muted/50 rounded-lg p-4">
                  <h4 className="font-medium mb-3">Transaction Summary</h4>
                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <span>Amount:</span>
                      <span className="font-medium">{amount} {selectedToken.symbol}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Network:</span>
                      <span>Polygon Mumbai (Testnet)</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Gas Fee:</span>
                      <span>~0.02 MATIC</span>
                    </div>
                    <Separator />
                    <div className="flex justify-between font-medium">
                      <span>Total:</span>
                      <span>{amount} {selectedToken.symbol}</span>
                    </div>
                  </div>
                </div>

                <div className="flex gap-2">
                  <Button variant="outline" onClick={() => setTransactionStep("input")} className="flex-1">
                    Back
                  </Button>
                  <Button onClick={confirmTransaction} className="flex-1">
                    Confirm & Pay
                  </Button>
                </div>
              </div>
            )}

            {transactionStep === "processing" && (
              <div className="text-center space-y-4">
                <div className="animate-spin h-8 w-8 border-4 border-primary border-t-transparent rounded-full mx-auto" />
                <div>
                  <h4 className="font-medium">Processing Transaction</h4>
                  <p className="text-sm text-muted-foreground">
                    Please wait while your transaction is being processed...
                  </p>
                </div>
              </div>
            )}
          </>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/payment/FiatPaymen.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { 
  CreditCard, 
  DollarSign, 
  ArrowRight, 
  CheckCircle, 
  AlertTriangle 
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface FiatPaymentProps {
  campaignId: string;
  onSuccess?: () => void;
}

export function FiatPayment({ campaignId, onSuccess }: FiatPaymentProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [amount, setAmount] = useState("");
  const [step, setStep] = useState<"input" | "processing" | "success">("input");

  const contributeMutation = useMutation({
    mutationFn: async ({ amount }: { amount: string }) => {
      return apiRequest("POST", "/api/contributions", {
        campaignId,
        amount,
        currency: "USD",
        paymentMethod: "fiat",
        transactionHash: `fiat_${Math.random().toString(36).substr(2, 9)}`,
      });
    },
    onSuccess: () => {
      toast({
        title: "Payment Successful!",
        description: "Your fiat contribution has been processed.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns", campaignId] });
      queryClient.invalidateQueries({ queryKey: ["/api/contributions"] });
      setStep("success");
      onSuccess?.();
    },
    onError: (error: any) => {
      toast({
        title: "Payment Failed",
        description: error.message || "Failed to process fiat payment.",
        variant: "destructive",
      });
      setStep("input");
    },
  });

  const handlePayment = async () => {
    if (!amount || parseFloat(amount) <= 0) {
      toast({
        title: "Invalid Amount",
        description: "Please enter a valid contribution amount.",
        variant: "destructive",
      });
      return;
    }

    setStep("processing");

    // Simulate payment processing
    setTimeout(() => {
      contributeMutation.mutate({ amount });
    }, 2000);
  };

  const resetForm = () => {
    setAmount("");
    setStep("input");
  };

  if (step === "success") {
    return (
      <Card className="w-full max-w-md mx-auto">
        <CardContent className="p-6 text-center">
          <CheckCircle className="h-16 w-16 text-green-500 mx-auto mb-4" />
          <h3 className="text-lg font-semibold mb-2">Payment Successful!</h3>
          <p className="text-muted-foreground mb-4">
            Your contribution of ${amount} USD has been processed.
          </p>
          <Button onClick={resetForm} className="w-full">
            Make Another Contribution
          </Button>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <CreditCard className="h-5 w-5" />
          Fiat Payment
        </CardTitle>
        <CardDescription>
          Support this campaign with traditional payment methods
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Payment Method Notice */}
        <div className="bg-amber-50 dark:bg-amber-950 rounded-lg p-3">
          <div className="flex items-center gap-2">
            <AlertTriangle className="h-4 w-4 text-amber-600" />
            <span className="text-sm font-medium text-amber-700 dark:text-amber-300">
              Demo Mode
            </span>
          </div>
          <p className="text-xs text-amber-600 dark:text-amber-400 mt-1">
            This is a demonstration. Real payments require Stripe integration.
          </p>
        </div>

        {step === "input" && (
          <>
            {/* Amount Input */}
            <div className="space-y-2">
              <Label htmlFor="fiat-amount">Contribution Amount</Label>
              <div className="relative">
                <DollarSign className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                <Input
                  id="fiat-amount"
                  type="number"
                  placeholder="0.00"
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                  className="pl-10 pr-16"
                />
                <Badge variant="secondary" className="absolute right-2 top-1/2 -translate-y-1/2">
                  USD
                </Badge>
              </div>
            </div>

            {/* Quick Amount Buttons */}
            <div className="grid grid-cols-3 gap-2">
              {["10", "50", "100"].map((quickAmount) => (
                <Button
                  key={quickAmount}
                  variant="outline"
                  size="sm"
                  onClick={() => setAmount(quickAmount)}
                >
                  ${quickAmount}
                </Button>
              ))}
            </div>

            {/* Mock Payment Form */}
            <div className="space-y-4">
              <Separator />
              <div className="space-y-3">
                <Label>Payment Details (Demo)</Label>
                <Input placeholder="Card Number: 4242 4242 4242 4242" disabled />
                <div className="grid grid-cols-2 gap-2">
                  <Input placeholder="MM/YY: 12/25" disabled />
                  <Input placeholder="CVC: 123" disabled />
                </div>
                <Input placeholder="Name on Card: John Doe" disabled />
              </div>
            </div>

            <Button onClick={handlePayment} className="w-full" disabled={!amount}>
              <CreditCard className="h-4 w-4 mr-2" />
              Pay ${amount || "0.00"} USD
            </Button>
          </>
        )}

        {step === "processing" && (
          <div className="text-center space-y-4">
            <div className="animate-spin h-8 w-8 border-4 border-primary border-t-transparent rounded-full mx-auto" />
            <div>
              <h4 className="font-medium">Processing Payment</h4>
              <p className="text-sm text-muted-foreground">
                Please wait while your payment is being processed...
              </p>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/payment/PaymentMod.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { CreditCard, Smartphone, Zap, X } from "lucide-react";
import { CryptoPayment } from "@/components/web3/CryptoPayment";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface PaymentModalProps {
  isOpen: boolean;
  onClose: () => void;
  campaign: {
    id: string;
    title: string;
    currentAmount: string;
    goalAmount: string;
    currency: string;
    smartContractAddress?: string;
  };
}

export function PaymentModal({ isOpen, onClose, campaign }: PaymentModalProps) {
  const [activeTab, setActiveTab] = useState("crypto");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const recordContributionMutation = useMutation({
    mutationFn: async (contributionData: any) => {
      return apiRequest("POST", "/api/contributions", contributionData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns", campaign.id] });
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns"] });
      queryClient.invalidateQueries({ queryKey: ["/api/stats"] });
      onClose();
    },
  });

  const handleCryptoSuccess = async (transactionHash: string) => {
    try {
      // Record the contribution in the database
      await recordContributionMutation.mutateAsync({
        campaignId: campaign.id,
        amount: "0.1", // This would come from the payment component
        currency: campaign.currency,
        transactionHash: transactionHash,
        paymentMethod: "crypto",
        status: "confirmed",
      });

      toast({
        title: "Contribution Recorded!",
        description: "Your crypto contribution has been successfully recorded on-chain.",
      });
    } catch (error) {
      toast({
        title: "Recording Failed",
        description: "Payment succeeded but failed to record. Please contact support.",
        variant: "destructive",
      });
    }
  };

  const FiatPayment = () => (
    <div className="space-y-6 p-4" data-testid="fiat-payment-section">
      <div className="text-center space-y-4">
        <div className="w-16 h-16 mx-auto rounded-full bg-blue-100 dark:bg-blue-900 flex items-center justify-center">
          <CreditCard className="w-8 h-8 text-blue-600" />
        </div>
        <div>
          <h3 className="text-lg font-semibold">Fiat Payment Coming Soon</h3>
          <p className="text-sm text-muted-foreground">
            Credit card and bank transfer payments will be available soon
          </p>
        </div>
      </div>

      <div className="space-y-3">
        <div className="p-4 border rounded-lg opacity-50">
          <div className="flex items-center space-x-3">
            <CreditCard className="w-5 h-5" />
            <div>
              <p className="font-medium">Credit/Debit Cards</p>
              <p className="text-sm text-muted-foreground">Visa, Mastercard, American Express</p>
            </div>
            <Badge variant="secondary">Soon</Badge>
          </div>
        </div>

        <div className="p-4 border rounded-lg opacity-50">
          <div className="flex items-center space-x-3">
            <Smartphone className="w-5 h-5" />
            <div>
              <p className="font-medium">Digital Wallets</p>
              <p className="text-sm text-muted-foreground">PayPal, Apple Pay, Google Pay</p>
            </div>
            <Badge variant="secondary">Soon</Badge>
          </div>
        </div>
      </div>

      <Button variant="outline" className="w-full" disabled data-testid="button-fiat-payment-disabled">
        Fiat Payments Coming Soon
      </Button>
    </div>
  );

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-2xl" data-testid="payment-modal">
        <DialogHeader>
          <div className="flex items-center justify-between">
            <DialogTitle className="flex items-center gap-2">
