            )}
            <Button 
              variant="ghost" 
              size="sm" 
              onClick={refreshBalance}
              disabled={loading}
              data-testid="button-refresh-balance"
            >
              <RefreshCw className={`h-3 w-3 ${loading ? 'animate-spin' : ''}`} />
