              <Button 
                variant="ghost" 
                onClick={refreshBalance}
                disabled={loading}
                data-testid="button-refresh-balance"
              >
                <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
