            <Button 
              onClick={connectWallet} 
              disabled={loading}
              className="w-full"
              data-testid="button-connect-wallet"
            >
              <Wallet className="h-4 w-4 mr-2" />
              {loading ? 'Connecting...' : 'Connect Avalanche Wallet'}
            </Button>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/hooks/useWallet.ts.content:
import { useState, useEffect } from 'react';
import { BrowserProvider, formatEther, parseEther } from 'ethers';
import { DEFAULT_NETWORK } from '@shared/contract';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import { useQueryClient } from '@tanstack/react-query';

interface WalletState {
  isConnected: boolean;
  address: string;
  balance: string;
  loading: boolean;
  error: string | null;
}

export function useWallet() {
  const [wallet, setWallet] = useState<WalletState>({
    isConnected: false,
    address: '',
    balance: '0',
    loading: false,
    error: null,
  });
  const { toast } = useToast();
  const queryClient = useQueryClient();

  useEffect(() => {
    // Check if already connected on mount
    checkConnection();
  }, []);

  const checkConnection = async () => {
    if (!window.ethereum) return;

    try {
      const provider = new BrowserProvider(window.ethereum);
      const accounts = await provider.listAccounts();

      if (accounts.length > 0) {
        const address = accounts[0].address;
        const balance = await provider.getBalance(address);

        setWallet({
          isConnected: true,
          address,
          balance: formatEther(balance),
          loading: false,
          error: null,
        });

        // Save wallet connection to user profile
        await saveWalletToProfile(address);
      }
    } catch (error) {
      console.error('Wallet check error:', error);
    }
  };

  const connectWallet = async () => {
    if (!window.ethereum) {
      setWallet(prev => ({ ...prev, error: 'MetaMask not found. Please install MetaMask.' }));
      toast({
        title: "MetaMask Required",
        description: "Please install MetaMask to connect your wallet",
        variant: "destructive",
      });
      return;
    }

    setWallet(prev => ({ ...prev, loading: true, error: null }));

    try {
      const provider = new BrowserProvider(window.ethereum);

      // Request account access
      await provider.send('eth_requestAccounts', []);

      // Check and switch network
      await switchToAvalanche();

      const signer = await provider.getSigner();
      const address = await signer.getAddress();
      const balance = await provider.getBalance(address);

      setWallet({
        isConnected: true,
        address,
        balance: formatEther(balance),
        loading: false,
        error: null,
      });

      // Save wallet connection to user profile
      await saveWalletToProfile(address);

      toast({
        title: "Wallet Connected",
        description: `Connected to ${address.slice(0, 6)}...${address.slice(-4)}`,
      });

    } catch (error: any) {
      setWallet(prev => ({ 
        ...prev, 
        loading: false, 
        error: error.message || 'Failed to connect wallet' 
      }));
      toast({
        title: "Connection Failed",
        description: error.message || "Failed to connect wallet",
        variant: "destructive",
      });
    }
  };

  const switchToAvalanche = async () => {
    try {
      await window.ethereum.request({
        method: 'wallet_switchEthereumChain',
        params: [{ chainId: '0xa869' }], // 43113 hex
      });
    } catch (error: any) {
      if (error.code === 4902) {
        // Network not added, add it
        await window.ethereum.request({
          method: 'wallet_addEthereumChain',
          params: [{
            chainId: '0xa869',
            chainName: DEFAULT_NETWORK.name,
            nativeCurrency: { name: 'AVAX', symbol: 'AVAX', decimals: 18 },
            rpcUrls: [DEFAULT_NETWORK.rpcHttpUrl],
            blockExplorerUrls: ['https://testnet.snowtrace.io/']
          }]
        });
      } else {
        throw error;
      }
    }
  };

  const saveWalletToProfile = async (address: string) => {
    try {
      await apiRequest('PUT', '/api/user/wallet', { walletAddress: address });
      queryClient.invalidateQueries({ queryKey: ['/api/user'] });
    } catch (error) {
      console.error('Failed to save wallet to profile:', error);
    }
  };

  const makePayment = async (campaignId: string, amount: string) => {
    if (!wallet.isConnected || !window.ethereum) {
      throw new Error('Wallet not connected');
    }

    const provider = new BrowserProvider(window.ethereum);
    const signer = await provider.getSigner();

    // Send transaction
    const tx = await signer.sendTransaction({
      to: '0x0000000000000000000000000000000000000000', // Burn address for demo
      value: parseEther(amount),
    });

    // Wait for confirmation
    const receipt = await tx.wait();

    if (!receipt) {
      throw new Error('Transaction failed');
    }

    // Log transaction in database
    const transactionData = {
      transactionHash: receipt.hash,
      amount: amount,
      walletAddress: wallet.address,
      campaignId: campaignId,
      status: 'completed',
    };

    await apiRequest('POST', '/api/transactions/avalanche', transactionData);

    // Invalidate queries to refresh transaction data
    queryClient.invalidateQueries({ queryKey: ['/api/transactions/avalanche'] });
    queryClient.invalidateQueries({ queryKey: ['/api/admin/transactions/avalanche'] });
    queryClient.invalidateQueries({ queryKey: ['/api/campaigns'] });

    // Update wallet balance
    const newBalance = await provider.getBalance(wallet.address);
    setWallet(prev => ({ ...prev, balance: formatEther(newBalance) }));

    // Invalidate relevant queries
    queryClient.invalidateQueries({ queryKey: ['/api/transactions'] });
    queryClient.invalidateQueries({ queryKey: ['/api/campaigns'] });

    return {
      hash: receipt.hash,
      amount,
      timestamp: new Date().toISOString(),
    };
  };

  const refreshBalance = async () => {
    if (!wallet.isConnected || !window.ethereum) return;

    try {
      const provider = new BrowserProvider(window.ethereum);
      const balance = await provider.getBalance(wallet.address);
      setWallet(prev => ({ ...prev, balance: formatEther(balance) }));
    } catch (error) {
      console.error('Failed to refresh balance:', error);
    }
  };

  const disconnectWallet = () => {
    setWallet({
      isConnected: false,
      address: '',
      balance: '0',
      loading: false,
      error: null,
    });
  };

  return {
    ...wallet,
    connectWallet,
    disconnectWallet,
    makePayment,
    refreshBalance,
  };
}

client/src/lib/contract.ts.content:
import { BrowserProvider, Contract, parseEther } from 'ethers';
import { CONTRACT_ADDRESS, CONTRACT_ABI, DEFAULT_NETWORK } from '../../../shared/contract';

declare global {
  interface Window { 
    ethereum?: {
      isMetaMask?: boolean;
      request: (args: { method: string; params?: any[] | Record<string, any> }) => Promise<any>;
      send?: (method: string, params?: any[]) => Promise<any>;
      on?: (event: string, callback: (data: any) => void) => void;
      removeListener?: (event: string, callback: (data: any) => void) => void;
    }
  }
}

export async function getProviderAndSigner() {
  if (!window.ethereum) throw new Error('Please install MetaMask or another Web3 wallet to continue');

  try {
    const provider = new BrowserProvider(window.ethereum);

    // Request account access first
    await provider.send('eth_requestAccounts', []);

    // Check current network
    const network = await provider.getNetwork();
    const currentChainId = Number(network.chainId);

    if (currentChainId !== DEFAULT_NETWORK.chainId) {
      // Attempt to switch to Avalanche Fuji testnet
      try {
        await window.ethereum!.request({
          method: 'wallet_switchEthereumChain',
          params: [{ chainId: '0xa869' }], // 43113 hex
        });
      } catch (switchError: any) {
        // If the network doesn't exist, add it
        if (switchError.code === 4902) {
          try {
            await window.ethereum!.request({
              method: 'wallet_addEthereumChain',
              params: [{
                chainId: '0xa869',
                chainName: DEFAULT_NETWORK.name,
                nativeCurrency: { name: 'AVAX', symbol: 'AVAX', decimals: 18 },
                rpcUrls: [DEFAULT_NETWORK.rpcHttpUrl],
                blockExplorerUrls: ['https://testnet.snowtrace.io/']
              }]
            });
          } catch (addError: any) {
            throw new Error(`Failed to add Avalanche Fuji network: ${addError.message || 'Unknown error'}`);
          }
        } else if (switchError.code === 4001) {
          throw new Error('Please switch to Avalanche Fuji testnet to continue');
        } else {
          throw new Error(`Failed to switch to Avalanche Fuji: ${switchError.message || 'Unknown error'}`);
        }
      }
    }

    const signer = await provider.getSigner();
    return { provider, signer };
  } catch (error: any) {
    if (error.code === 4001) {
      throw new Error('Please connect your wallet to continue');
    }
    throw error;
  }
}

export async function getReadOnlyContract() {
  // Read via your backend (recommended) OR direct RPC:
  if (!window.ethereum) throw new Error('MetaMask not found');
  const provider = new BrowserProvider(window.ethereum);
  return new Contract(CONTRACT_ADDRESS, CONTRACT_ABI, provider);
}

export async function getWriteContract() {
  const { signer } = await getProviderAndSigner();
  return new Contract(CONTRACT_ADDRESS, CONTRACT_ABI, signer);
}

// Actions
export async function fund(amountEth: string) {
  try {
    // Validate amount
    const amount = parseFloat(amountEth);
    if (isNaN(amount) || amount <= 0) {
      throw new Error('Please enter a valid funding amount greater than 0');
    }

    // Get contract instance
    const c = await getWriteContract();

    // Check wallet balance
    const { signer } = await getProviderAndSigner();
    const balance = await signer.provider.getBalance(signer.address);
    const requiredAmount = parseEther(amountEth);

    if (balance < requiredAmount) {
      throw new Error(`Insufficient balance. Required: ${amountEth} AVAX, Available: ${(Number(balance) / 1e18).toFixed(4)} AVAX`);
    }

    // Send transaction with proper gas estimation
    const tx = await c.fund({ 
      value: requiredAmount,
      gasLimit: 100000 // Set reasonable gas limit
    });

    // Wait for confirmation
    const receipt = await tx.wait();

    if (!receipt || !receipt.hash) {
      throw new Error('Transaction failed - no receipt received');
    }

    // Save transaction to database
    try {
      const walletAddress = await signer.getAddress();

      const response = await fetch(`${window.location.origin}/api/public/transactions/avalanche`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          transactionHash: receipt.hash,
          amount: amountEth,
          walletAddress,
          campaignId: 'contract-demo',
          status: 'completed',
          transactionType: 'funding'
        })
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.warn('Failed to save transaction to database:', errorText);
      } else {
        console.log('Successfully saved funding transaction to database');
      }
    } catch (error) {
      console.warn('Failed to save transaction to database:', error);
      // Don't throw here - transaction succeeded even if DB save failed
    }

    return receipt;
  } catch (error: any) {
    // Provide more specific error messages
    if (error.code === 4001) {
      throw new Error('Transaction was rejected by user');
    } else if (error.code === -32603) {
      throw new Error('Transaction failed - please check your wallet balance and network connection');
    } else if (error.reason) {
      throw new Error(`Smart contract error: ${error.reason}`);
    } else if (error.message) {
      throw error;
    } else {
      throw new Error('Transaction failed - unknown error occurred');
    }
  }
}

export async function completeMilestone() {
  try {
    const c = await getWriteContract();
    const tx = await c.completeMilestone({ gasLimit: 100000 });
    const receipt = await tx.wait();

    if (!receipt || !receipt.hash) {
      throw new Error('Transaction failed - no receipt received');
    }

    // Save transaction to database
    try {
      const { signer } = await getProviderAndSigner();
      const walletAddress = await signer.getAddress();

      const response = await fetch(`${window.location.origin}/api/public/transactions/avalanche`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          transactionHash: receipt.hash,
          amount: '0',
          walletAddress,
          campaignId: 'milestone-completion',
          status: 'completed',
          transactionType: 'milestone'
        })
      });

      if (!response.ok) {
        console.warn('Failed to save transaction to database:', await response.text());
      }
    } catch (error) {
      console.warn('Failed to save transaction to database:', error);
    }

    return receipt;
  } catch (error: any) {
    if (error.code === 4001) {
      throw new Error('Transaction was rejected by user');
    } else if (error.reason) {
      throw new Error(`Smart contract error: ${error.reason}`);
    } else if (error.message) {
      throw error;
    } else {
      throw new Error('Failed to complete milestone - unknown error occurred');
    }
  }
}

// Views
export async function fetchStateFromServer() {
  const res = await fetch(`${window.location.origin}/api/contract/state`);
  if (!res.ok) throw new Error('Server state fetch failed');
  return res.json();
}

export async function getBackerAmount(addr: string) {
  const res = await fetch(`${window.location.origin}/api/contract/backers/${addr}`);
  if (!res.ok) throw new Error('Backer fetch failed');
  return res.json();
}

// Live events via SSE
export function subscribeEvents(onEvent: (type: string, payload: any) => void) {
  const es = new EventSource(`${window.location.origin}/api/contract/events`);
  es.addEventListener('Funded', (e: MessageEvent) => onEvent('Funded', JSON.parse(e.data)));
  es.addEventListener('MilestoneCompleted', (e: MessageEvent) => onEvent('MilestoneCompleted', JSON.parse(e.data)));
  es.addEventListener('error', (e) => {
    console.error('SSE Error:', e);
  });
  return () => es.close();
}

