import React, { useEffect, useMemo, useState } from 'react';
import { fetchStateFromServer, fund, completeMilestone, subscribeEvents, getBackerAmount } from '../lib/contract';
import { getProviderAndSigner } from '../lib/contract';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Wallet, TrendingUp, CheckCircle, RefreshCw } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

function weiToEth(wei: string) {
  return (Number(wei) / 1e18).toString();
}

export default function ContractDemo() {
  const [account, setAccount] = useState<string>('');
  const [state, setState] = useState<any>(null);
  const [amount, setAmount] = useState<string>('0.01');
  const [log, setLog] = useState<string[]>([]);
  const [myContribution, setMyContribution] = useState<string>('0');
  const [loading, setLoading] = useState(false);
  const [campaigns, setCampaigns] = useState<any[]>([]);
  const [selectedCampaign, setSelectedCampaign] = useState<string>('');
  const { toast } = useToast();

  useEffect(() => {
    const load = async () => {
      try {
        const [s, campaignsRes] = await Promise.all([
          fetchStateFromServer(),
          fetch('/api/campaigns').then(r => r.json())
        ]);
        setState(s);
        setCampaigns(campaignsRes);
        if (campaignsRes.length > 0) {
          setSelectedCampaign(campaignsRes[0].id);
        }
      } catch (error) {
        console.error('Failed to load data:', error);
        toast({
          title: "Error",
          description: "Failed to load contract state or campaigns",
          variant: "destructive",
        });
      }
    };
    load();

    const unsub = subscribeEvents((type, p) => {
      const timestamp = new Date().toLocaleTimeString();
      setLog(prev => [`${timestamp}: ${type} ${JSON.stringify(p)}`, ...prev].slice(0, 10));

      // refresh on any event
      fetchStateFromServer().then(setState).catch(() => {});
      if (account) {
        getBackerAmount(account).then((x) => setMyContribution(x.amount)).catch(() => {});
      }

      toast({
        title: `Contract Event: ${type}`,
        description: `${JSON.stringify(p)}`,
      });
    });

    return unsub;
  }, [account, toast]);

  const goalEth = useMemo(() => state ? weiToEth(state.fundingGoal) : '0', [state]);
  const totalEth = useMemo(() => state ? weiToEth(state.totalFunded) : '0', [state]);
  const balEth = useMemo(() => state ? weiToEth(state.contractBalance) : '0', [state]);
  const progressPercent = useMemo(() => {
    if (!state || !state.fundingGoal) return 0;
    return Math.min((Number(totalEth) / Number(goalEth)) * 100, 100);
  }, [totalEth, goalEth]);

  const connect = async () => {
    try {
      setLoading(true);
      const { signer } = await getProviderAndSigner();
      const addr = await signer.getAddress();
      setAccount(addr);

      const b = await getBackerAmount(addr);
      setMyContribution(b.amount);

      toast({
        title: "Wallet Connected",
        description: `Connected to ${addr.slice(0, 6)}...${addr.slice(-4)}`,
      });
    } catch (error: any) {
      toast({
        title: "Connection Failed",
        description: error.message || "Failed to connect wallet",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleFund = async () => {
    try {
      if (!selectedCampaign) {
        toast({
          title: "No Campaign Selected",
          description: "Please select a campaign to fund",
          variant: "destructive",
        });
        return;
      }

      setLoading(true);
      const receipt = await fund(amount);

      // Update the campaign funding immediately after successful transaction
      try {
        const response = await fetch(`/api/campaigns/${selectedCampaign}/fund`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            amount: parseFloat(amount),
            transactionHash: receipt.hash,
            walletAddress: account
          })
        });

        if (response.ok) {
          toast({
            title: "Funding Successful",
            description: `Funded ${amount} AVAX to campaign successfully! Transaction: ${receipt.hash}`,
          });
        } else {
          console.warn('Failed to update campaign funding');
        }
      } catch (updateError) {
        console.warn('Failed to update campaign:', updateError);
      }

      // Add to log for immediate feedback
      setLog(prev => [`${new Date().toLocaleTimeString()}: Fund transaction ${receipt.hash}`, ...prev].slice(0, 10));
    } catch (error: any) {
      toast({
        title: "Funding Failed",
        description: error.message || "Failed to fund campaign",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleCompleteMilestone = async () => {
    try {
      setLoading(true);
      const receipt = await completeMilestone();
      toast({
        title: "Milestone Completed",
        description: `Milestone completed successfully! Transaction: ${receipt.hash}`,
      });
      // Add to log for immediate feedback
      setLog(prev => [`${new Date().toLocaleTimeString()}: Milestone transaction ${receipt.hash}`, ...prev].slice(0, 10));
    } catch (error: any) {
      toast({
        title: "Milestone Failed",
        description: error.message || "Failed to complete milestone",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };



  return (
    <div className="container mx-auto py-8 px-4 max-w-4xl">
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold mb-4 bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
          Avalanche Crowdfund dApp
        </h1>
        <p className="text-muted-foreground">
          Connect to Avalanche Fuji testnet and interact with the smart contract
        </p>
      </div>

      {/* Wallet Connection */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Wallet className="h-5 w-5" />
            Wallet Connection
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center gap-4">
            <Button 
              onClick={connect} 
              disabled={!!account || loading}
              className="flex items-center gap-2"
            >
              {loading && <RefreshCw className="h-4 w-4 animate-spin" />}
              {account ? `Connected: ${account.slice(0,6)}…${account.slice(-4)}` : 'Connect Wallet'}
            </Button>
            {account && (
              <Badge variant="outline" className="text-green-600">
                Connected to Avalanche Fuji
              </Badge>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Campaign State */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <TrendingUp className="h-5 w-5" />
            Campaign State
          </CardTitle>
        </CardHeader>
        <CardContent>
          {!state ? (
            <div className="flex items-center gap-2">
              <RefreshCw className="h-4 w-4 animate-spin" />
              <span>Loading campaign data...</span>
            </div>
          ) : (
            <div className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-muted-foreground">Creator</p>
                  <p className="font-mono text-sm">{state.creator}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Deadline (Unix)</p>
                  <p className="font-mono">{state.deadline}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Funding Goal</p>
                  <p className="text-xl font-semibold">{goalEth} AVAX</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Total Funded</p>
                  <p className="text-xl font-semibold text-green-600">{totalEth} AVAX</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Contract Balance</p>
                  <p className="text-xl font-semibold">{balEth} AVAX</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Progress</p>
                  <div className="flex items-center gap-2">
                    <div className="flex-1 bg-gray-200 rounded-full h-2 dark:bg-gray-700">
                      <div 
                        className="bg-green-600 h-2 rounded-full transition-all duration-300" 
                        style={{ width: `${progressPercent}%` }}
                      ></div>
                    </div>
                    <span className="text-sm font-medium">{progressPercent.toFixed(1)}%</span>
                  </div>
                </div>
              </div>

              <Separator />

              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">Goal Reached</p>
                  <Badge variant={state.goalReached ? "default" : "secondary"}>
                    {state.goalReached ? "✅ Yes" : "❌ No"}
                  </Badge>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Milestones</p>
                  <Badge variant="outline">
                    {state.milestonesCompleted}/{state.milestoneCount}
                  </Badge>
                </div>
              </div>

              {account && (
                <div className="pt-2 border-t">
                  <p className="text-sm text-muted-foreground">Your Contribution</p>
                  <p className="text-lg font-semibold text-blue-600">
                    {weiToEth(myContribution)} AVAX
                  </p>
                </div>
              )}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Actions */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle>Contract Actions</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div className="space-y-3">
              <div>
                <label className="text-sm text-muted-foreground">Select Campaign to Fund</label>
                <Select value={selectedCampaign} onValueChange={setSelectedCampaign}>
                  <SelectTrigger>
                    <SelectValue placeholder="Choose a campaign" />
                  </SelectTrigger>
                  <SelectContent>
                    {campaigns.map((campaign) => (
                      <SelectItem key={campaign.id} value={campaign.id}>
                        {campaign.title} - Goal: ${campaign.goalAmount}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="flex flex-col sm:flex-row gap-4 items-end">
                <div className="flex-1">
                  <label className="text-sm text-muted-foreground">Amount (AVAX)</label>
                  <Input
                    type="number"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    placeholder="0.01"
                    step="0.01"
                    min="0"
                  />
                </div>
                <div className="flex gap-2">
                  <Button 
                    onClick={handleFund}
                    disabled={!account || loading || !selectedCampaign}
                    className="flex items-center gap-2"
                  >
                    {loading && <RefreshCw className="h-4 w-4 animate-spin" />}
                    Fund Campaign
                  </Button>
                </div>
              </div>
            </div>

            <div className="flex gap-2">
              <Button 
                onClick={handleCompleteMilestone}
                disabled={!account || loading}
                variant="outline"
                className="flex items-center gap-2"
              >
                {loading && <RefreshCw className="h-4 w-4 animate-spin" />}
                <CheckCircle className="h-4 w-4" />
                Complete Milestone
              </Button>

            </div>

            <p className="text-xs text-muted-foreground">
              Note: Funding will be added to the selected campaign automatically. Complete milestone permissions depend on contract logic.
            </p>
          </div>
        </CardContent>
      </Card>

      {/* Live Events */}
      <Card>
        <CardHeader>
          <CardTitle>Live Contract Events</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-2 max-h-64 overflow-y-auto">
            {log.length === 0 ? (
              <p className="text-muted-foreground text-sm">No events yet. Interact with the contract to see live updates.</p>
            ) : (
              log.map((l, i) => (
                <div key={i} className="p-2 bg-muted rounded text-sm font-mono">
                  {l}
                </div>
              ))
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/live-transactions.tsx.content:
import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { MainNavigation } from "@/components/navigation/MainNavigation";
import { ThreeBackground } from "@/components/three/ThreeBackground";
import Footer from "@/components/layout/footer";
import { useToast } from "@/hooks/use-toast";
import type { Transaction } from "@shared/schema";
import { 
  Activity, 
  Zap, 
  TrendingUp, 
  Clock,
  ExternalLink,
  Wifi,
  Volume2,
  VolumeX,
  Brain,
  BarChart3,
  Target,
  Lightbulb,
  Sparkles
} from "lucide-react";
import { formatDistanceToNow } from "date-fns";

interface LiveStats {
  totalTransactions: number;
  totalVolume: number;
  averageAmount: number;
  transactionsPerMinute: number;
}

export default function LiveTransactions() {
  const [isConnected, setIsConnected] = useState(false);
  const [soundEnabled, setSoundEnabled] = useState(true);
  const [liveStats, setLiveStats] = useState<LiveStats>({
    totalTransactions: 0,
    totalVolume: 0,
    averageAmount: 0,
    transactionsPerMinute: 0
  });
  const { toast } = useToast();

  const { data: transactions = [] } = useQuery<Transaction[]>({
    queryKey: ["/api/transactions"],
    refetchInterval: 1000, // Update every second for real-time feel
  });

  interface AvalancheTransaction {
    id: string;
    transactionHash: string;
    amount: string;
    walletAddress: string;
    campaignId: string;
    status: string;
    createdAt: string;
    transactionType?: string; // Added for compatibility
    user?: {
      username: string;
      email: string;
    };
    campaign?: {
      title: string;
    };
  }

  const { data: avalancheTransactions = [] } = useQuery<AvalancheTransaction[]>({
    queryKey: ["/api/public/transactions/avalanche"],
    refetchInterval: 1000,
  });

  // WebSocket connection for instant updates (with error handling)
  useEffect(() => {
    let ws: WebSocket | null = null;

    try {
      const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
      const wsUrl = `${protocol}//${window.location.host}/ws/admin`;

      ws = new WebSocket(wsUrl);

      ws.onopen = () => {
        setIsConnected(true);
        ws?.send(JSON.stringify({ type: 'authenticate', role: 'user' }));
      };

      ws.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data);

          if (data.event === 'transaction_created') {
            if (soundEnabled) {
              // Play notification sound
              const audio = new Audio('data:audio/wav;base64,');
              audio.volume = 0.1;
              audio.play().catch(() => {}); // Ignore errors if audio can't play
            }

            toast({
              title: "New Transaction!",
              description: `${data.data?.transactionType?.toUpperCase() || 'TRANSACTION'} - ${data.data?.amount || '0'} ETH`,
              className: "border-cyber-green",
            });
          }
        } catch (error) {
          console.warn('Failed to parse WebSocket message:', error);
        }
      };

      ws.onclose = () => setIsConnected(false);
      ws.onerror = (error) => {
        console.warn('WebSocket connection failed:', error);
        setIsConnected(false);
      };
    } catch (error) {
      console.warn('Failed to create WebSocket connection:', error);
      setIsConnected(false);
    }

    return () => {
      if (ws) {
        try {
          ws.close();
        } catch (error) {
          console.warn('Failed to close WebSocket:', error);
        }
      }
    };
  }, [soundEnabled, toast]);

  // Calculate live stats
  useEffect(() => {
    const allTransactions = [...transactions, ...avalancheTransactions];
    const totalVolume = allTransactions.reduce((sum, tx) => sum + parseFloat(tx.amount || "0"), 0);
    const avgAmount = allTransactions.length > 0 ? totalVolume / allTransactions.length : 0;

    setLiveStats({
      totalTransactions: allTransactions.length,
      totalVolume,
      averageAmount: avgAmount,
      transactionsPerMinute: Math.floor(Math.random() * 15) + 5 // Mock real-time rate
    });
  }, [transactions, avalancheTransactions]);

  const getTransactionColor = (type: string) => {
    const colors: Record<string, string> = {
      contribution: "bg-green-500/20 text-green-400 border-green-500/20",
      withdrawal: "bg-red-500/20 text-red-400 border-red-500/20",
      contract_creation: "bg-blue-500/20 text-blue-400 border-blue-500/20",
    };
    return colors[type] || "bg-gray-500/20 text-gray-400 border-gray-500/20";
  };

  return (
    <div className="min-h-screen bg-background">
      <ThreeBackground />
      <MainNavigation />

      <div className="relative z-10 pt-20 pb-10">
        <section className="py-10">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            {/* Header */}
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="text-center mb-10"
            >
              <div className="flex items-center justify-center gap-4 mb-4">
                <h1 className="text-4xl md:text-6xl font-black gradient-text">
                  Live Transactions
                </h1>
                <div className="flex items-center gap-2">
                  {isConnected ? (
                    <div className="flex items-center gap-2 text-green-400">
                      <Wifi className="w-5 h-5" />
                      <span className="text-sm">Connected</span>
                    </div>
                  ) : (
                    <div className="flex items-center gap-2 text-red-400">
                      <Wifi className="w-5 h-5" />
                      <span className="text-sm">Disconnected</span>
                    </div>
                  )}

                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setSoundEnabled(!soundEnabled)}
                  >
                    {soundEnabled ? <Volume2 className="w-4 h-4" /> : <VolumeX className="w-4 h-4" />}
                  </Button>
                </div>
              </div>
              <p className="text-xl text-muted-foreground">
                Watch blockchain transactions happen in real-time
              </p>
            </motion.div>

            {/* Live Stats */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-10">
              <Card className="glass-morphism border-cyber-green/20">
                <CardContent className="p-6">
                  <div className="flex items-center gap-3">
                    <Activity className="w-8 h-8 text-cyber-green" />
                    <div>
                      <div className="text-2xl font-bold">{liveStats.totalTransactions}</div>
                      <div className="text-sm text-muted-foreground">Total Transactions</div>
                    </div>
                  </div>
                </CardContent>
              </Card>

              <Card className="glass-morphism border-cyber-blue/20">
                <CardContent className="p-6">
                  <div className="flex items-center gap-3">
                    <TrendingUp className="w-8 h-8 text-cyber-blue" />
                    <div>
                      <div className="text-2xl font-bold">{liveStats.totalVolume.toFixed(2)}</div>
                      <div className="text-sm text-muted-foreground">Total Volume (ETH)</div>
                    </div>
                  </div>
                </CardContent>
              </Card>

              <Card className="glass-morphism border-cyber-purple/20">
                <CardContent className="p-6">
                  <div className="flex items-center gap-3">
                    <Zap className="w-8 h-8 text-cyber-purple" />
                    <div>
                      <div className="text-2xl font-bold">{liveStats.averageAmount.toFixed(4)}</div>
                      <div className="text-sm text-muted-foreground">Avg Amount (ETH)</div>
                    </div>
                  </div>
                </CardContent>
              </Card>

              <Card className="glass-morphism border-cyber-yellow/20">
                <CardContent className="p-6">
                  <div className="flex items-center gap-3">
                    <Clock className="w-8 h-8 text-cyber-yellow animate-pulse" />
                    <div>
                      <div className="text-2xl font-bold">{liveStats.transactionsPerMinute}</div>
                      <div className="text-sm text-muted-foreground">Tx/Min</div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Transaction Feed */}
            <Tabs defaultValue="all" className="w-full">
              <TabsList className="grid w-full grid-cols-4">
                <TabsTrigger value="all">All Transactions</TabsTrigger>
                <TabsTrigger value="ethereum">Ethereum</TabsTrigger>
                <TabsTrigger value="avalanche">Avalanche</TabsTrigger>
                <TabsTrigger value="ai-analysis">AI Analysis</TabsTrigger>
              </TabsList>

              <TabsContent value="all" className="mt-8">
                <Card className="glass-morphism">
                  <CardHeader>
                    <CardTitle className="flex items-center gap-3">
                      <div className="relative">
                        <div className="w-3 h-3 bg-cyber-green rounded-full animate-pulse"></div>
                        <div className="absolute top-0 left-0 w-3 h-3 bg-cyber-green rounded-full animate-ping opacity-75"></div>
                      </div>
                      All Network Transactions
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4 max-h-[600px] overflow-y-auto">
                      <AnimatePresence>
                        {[...transactions, ...avalancheTransactions]
                          .sort((a, b) => new Date(b.createdAt || 0).getTime() - new Date(a.createdAt || 0).getTime())
                          .slice(0, 20)
                          .map((tx, index) => (
                          <motion.div
                            key={tx.id}
                            initial={{ opacity: 0, x: -20, scale: 0.9 }}
                            animate={{ opacity: 1, x: 0, scale: 1 }}
                            exit={{ opacity: 0, x: 20, scale: 0.9 }}
                            transition={{ duration: 0.3, delay: index * 0.05 }}
                            className="bg-muted/30 rounded-xl p-4 border border-white/10 hover:bg-muted/50 transition-all"
                          >
                            <div className="flex justify-between items-start mb-2">
                              <Badge className={getTransactionColor(tx.transactionType || 'avalanche')}>
                                {(tx.transactionType || 'AVALANCHE').toUpperCase()}
                              </Badge>
                              <div className="text-xs text-muted-foreground">
                                {tx.createdAt ? formatDistanceToNow(new Date(tx.createdAt), { addSuffix: true }) : 'Unknown time'}
                              </div>
                            </div>

                            <div className="flex justify-between items-center">
                              <div className="font-mono text-lg font-bold">
                                {parseFloat(tx.amount || "0").toFixed(4)} {'transactionHash' in tx ? 'AVAX' : 'ETH'}
                              </div>

                              <Button 
                                variant="ghost" 
                                size="sm"
                                onClick={() => {
                                  const explorerUrl = 'transactionHash' in tx 
                                    ? `https://testnet.snowtrace.io/tx/${tx.transactionHash}`
                                    : `https://etherscan.io/tx/${tx.id}`;
                                  window.open(explorerUrl, '_blank');
                                }}
                              >
                                <ExternalLink className="w-4 h-4" />
                              </Button>
                            </div>
                          </motion.div>
                        ))}
                      </AnimatePresence>

                      {transactions.length === 0 && avalancheTransactions.length === 0 && (
                        <div className="text-center py-12">
                          <Activity className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                          <p className="text-muted-foreground">No transactions yet. Start funding campaigns to see live activity!</p>
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>
              </TabsContent>

              <TabsContent value="ethereum">
                <Card className="glass-morphism">
                  <CardHeader>
                    <CardTitle>Ethereum Transactions</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4 max-h-[600px] overflow-y-auto">
                      {transactions.length === 0 ? (
                        <div className="text-center py-12">
                          <Activity className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                          <p className="text-muted-foreground">No Ethereum transactions yet.</p>
                        </div>
                      ) : (
                        transactions.map((tx, index) => (
                          <motion.div
                            key={tx.id}
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.3, delay: index * 0.1 }}
                            className="bg-muted/30 rounded-xl p-4 border border-white/10"
                          >
                            <div className="flex justify-between items-center">
                              <Badge className={getTransactionColor(tx.transactionType)}>
                                {tx.transactionType.toUpperCase()}
                              </Badge>
                              <div className="font-mono text-lg font-bold">
                                {parseFloat(tx.amount || "0").toFixed(4)} ETH
                              </div>

                              <Button 
                                variant="ghost" 
                                size="sm"
                                onClick={() => {
                                  window.open(`https://etherscan.io/tx/${tx.id}`, '_blank');
                                }}
                              >
                                <ExternalLink className="w-4 h-4" />
                              </Button>
                            </div>
                          </motion.div>
                        ))
                      )}
                    </div>
                  </CardContent>
                </Card>
              </TabsContent>

              <TabsContent value="avalanche">
                <Card className="glass-morphism">
                  <CardHeader>
                    <CardTitle>Avalanche Transactions</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4 max-h-[600px] overflow-y-auto">
                      {avalancheTransactions.length === 0 ? (
                        <div className="text-center py-12">
                          <Activity className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                          <p className="text-muted-foreground">No Avalanche transactions yet.</p>
                        </div>
                      ) : (
                        avalancheTransactions.map((tx: any, index: number) => (
