                              <Badge className="bg-red-500/20 text-red-400 border-red-500/20">
                                AVALANCHE
                              </Badge>
                              <div className="font-mono text-lg font-bold">
                                {parseFloat(tx.amount || "0").toFixed(4)} AVAX
