client/src/components/ui/copyable-hash.t.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Tooltip, TooltipContent, TooltipTrigger } from "@/components/ui/tooltip";
import { Copy, Check } from "lucide-react";
import { cn } from "@/lib/utils";

interface CopyableHashProps {
  hash: string;
  shorten?: boolean;
  className?: string;
}

export function CopyableHash({ hash, shorten = true, className }: CopyableHashProps) {
  const [copied, setCopied] = useState(false);

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(hash);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error("Failed to copy:", err);
    }
  };

  const displayHash = shorten && hash.length > 12 
    ? `${hash.slice(0, 6)}...${hash.slice(-4)}`
    : hash;

  return (
    <div className={cn("flex items-center gap-2", className)}>
      <code className="font-mono text-sm bg-muted/30 px-2 py-1 rounded">
        {displayHash}
      </code>
      <Tooltip>
        <TooltipTrigger asChild>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleCopy}
            className="h-6 w-6 p-0"
          >
            {copied ? (
              <Check className="h-3 w-3 text-success" />
            ) : (
              <Copy className="h-3 w-3" />
            )}
          </Button>
        </TooltipTrigger>
        <TooltipContent>
          <p>{copied ? "Copied!" : "Copy to clipboard"}</p>
        </TooltipContent>
      </Tooltip>
    </div>
  );
}

client/src/components/ui/kyc-badge.tsx.content:
import { Badge } from "@/components/ui/badge";
import { Tooltip, TooltipContent, TooltipTrigger } from "@/components/ui/tooltip";
import { CheckCircle, Clock, XCircle } from "lucide-react";

interface KYCBadgeProps {
  status: "approved" | "pending" | "rejected";
  className?: string;
}

export function KYCBadge({ status, className }: KYCBadgeProps) {
  const getStatusConfig = () => {
    switch (status) {
      case "approved":
        return {
          icon: CheckCircle,
          label: "KYC Approved",
          className: "bg-success/20 text-success border-success/30",
          tooltip: "Your identity has been verified. You can now create campaigns and access all features."
        };
      case "pending":
        return {
          icon: Clock,
          label: "KYC Pending",
          className: "bg-warning/20 text-warning border-warning/30",
          tooltip: "Your KYC verification is under review. This usually takes 1-3 business days."
        };
      case "rejected":
        return {
          icon: XCircle,
          label: "KYC Rejected",
          className: "bg-danger/20 text-danger border-danger/30",
          tooltip: "Your KYC verification was rejected. Please resubmit with correct documents."
        };
    }
  };

  const config = getStatusConfig();
  const Icon = config.icon;

  return (
    <Tooltip>
      <TooltipTrigger>
        <Badge className={`${config.className} ${className}`}>
          <Icon className="w-3 h-3 mr-1" />
          {config.label}
        </Badge>
      </TooltipTrigger>
      <TooltipContent>
        <p className="max-w-xs">{config.tooltip}</p>
      </TooltipContent>
    </Tooltip>
  );
}

