import OpenAI from "openai";

// the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR || "default_key"
});

const hasValidApiKey = () => {
  const apiKey = process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR;
  return apiKey && apiKey !== "default_key" && apiKey.startsWith("sk-");
};

export interface CampaignOptimization {
  titleSuggestions: string[];
  descriptionEnhancements: string[];
  fundingPrediction: {
    successProbability: number;
    recommendedGoal: number;
    estimatedDuration: number;
  };
  credibilityScore: number;
  marketAnalysis: {
    similarCampaigns: number;
    categoryTrends: string;
    competitionLevel: string;
  };
}

export async function optimizeCampaignTitle(title: string): Promise<{
  suggestions: string[];
  engagementBoost: string;
}> {
  if (!hasValidApiKey()) {
    return {
      suggestions: [
        `Enhanced: ${title}`,
        `Professional: ${title}`,
        `Innovative: ${title}`
      ],
      engagementBoost: "AI optimization unavailable - requires OpenAI API key"
    };
  }

  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: "You are a crowdfunding optimization expert. Analyze campaign titles and provide 3 improved alternatives that increase engagement. Respond with JSON in this format: { 'suggestions': ['title1', 'title2', 'title3'], 'engagementBoost': 'percentage increase estimate' }",
        },
        {
          role: "user",
          content: `Optimize this crowdfunding campaign title: "${title}"`,
        },
      ],
      response_format: { type: "json_object" },
    });

    const content = response.choices[0]?.message?.content;
    if (!content) {
      throw new Error("No content received from OpenAI");
    }
    const result = JSON.parse(content);
    return {
      suggestions: result.suggestions || [],
      engagementBoost: result.engagementBoost || "0%",
    };
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : "Unknown error";
    throw new Error("Failed to optimize campaign title: " + errorMessage);
  }
}

export async function enhanceCampaignDescription(description: string, category: string): Promise<{
  enhancedDescription: string;
  keyImprovements: string[];
}> {
  if (!hasValidApiKey()) {
    return {
      enhancedDescription: description,
      keyImprovements: ["AI enhancement unavailable - requires OpenAI API key"]
    };
  }

  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: "You are a crowdfunding copywriting expert. Enhance campaign descriptions to be more compelling and persuasive. Focus on clear value propositions, emotional connection, and credibility. Respond with JSON in this format: { 'enhancedDescription': 'improved text', 'keyImprovements': ['improvement1', 'improvement2'] }",
        },
        {
          role: "user",
          content: `Enhance this ${category} crowdfunding campaign description: "${description}"`,
        },
      ],
      response_format: { type: "json_object" },
    });

    const content = response.choices[0]?.message?.content;
    if (!content) {
      throw new Error("No content received from OpenAI");
    }
    const result = JSON.parse(content);
    return {
      enhancedDescription: result.enhancedDescription || description,
      keyImprovements: result.keyImprovements || [],
    };
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : "Unknown error";
    throw new Error("Failed to enhance campaign description: " + errorMessage);
  }
}

export async function analyzeCampaignCredibility(campaignData: {
  title: string;
  description: string;
  category: string;
  goalAmount: number;
  creatorKycStatus: string;
}): Promise<{
  score: number;
  factors: {
    factor: string;
    impact: string;
    score: number;
  }[];
  recommendations: string[];
}> {
  if (!hasValidApiKey()) {
    return {
      score: campaignData.creatorKycStatus === "verified" ? 7.5 : 5.0,
      factors: [
        { factor: "KYC Status", impact: campaignData.creatorKycStatus === "verified" ? "positive" : "negative", score: campaignData.creatorKycStatus === "verified" ? 8 : 4 },
        { factor: "Goal Amount", impact: "neutral", score: 6 },
        { factor: "Description Length", impact: campaignData.description.length > 100 ? "positive" : "negative", score: campaignData.description.length > 100 ? 7 : 5 }
      ],
      recommendations: ["AI analysis unavailable - requires OpenAI API key"]
    };
  }

  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: "You are a crowdfunding credibility analyst. Analyze campaigns and provide credibility scores (0-10) based on various factors. Respond with JSON in this format: { 'score': number, 'factors': [{'factor': 'string', 'impact': 'positive/negative/neutral', 'score': number}], 'recommendations': ['rec1', 'rec2'] }",
        },
        {
          role: "user",
          content: `Analyze the credibility of this campaign: Title: "${campaignData.title}", Description: "${campaignData.description}", Category: ${campaignData.category}, Goal: $${campaignData.goalAmount}, KYC Status: ${campaignData.creatorKycStatus}`,
        },
      ],
      response_format: { type: "json_object" },
    });

    const content = response.choices[0]?.message?.content;
    if (!content) {
      throw new Error("No content received from OpenAI");
    }
    const result = JSON.parse(content);
    return {
      score: Math.max(0, Math.min(10, result.score || 0)),
      factors: result.factors || [],
      recommendations: result.recommendations || [],
    };
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : "Unknown error";
    throw new Error("Failed to analyze campaign credibility: " + errorMessage);
  }
}

export async function predictFundingSuccess(campaignData: {
  title: string;
  description: string;
  category: string;
  goalAmount: number;
  duration: number;
}): Promise<{
  successProbability: number;
  recommendedGoal: number;
  marketInsights: string[];
}> {
  if (!hasValidApiKey()) {
    return {
      successProbability: Math.min(0.85, Math.max(0.45, 0.70 - (campaignData.goalAmount / 100000))),
      recommendedGoal: campaignData.goalAmount,
      marketInsights: ["AI prediction unavailable - requires OpenAI API key"]
    };
  }

  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: "You are a crowdfunding market analyst. Predict funding success probability and provide optimization recommendations. Respond with JSON in this format: { 'successProbability': number (0-1), 'recommendedGoal': number, 'marketInsights': ['insight1', 'insight2'] }",
        },
        {
          role: "user",
          content: `Analyze funding potential: Category: ${campaignData.category}, Goal: $${campaignData.goalAmount}, Duration: ${campaignData.duration} days, Title: "${campaignData.title}"`,
        },
      ],
      response_format: { type: "json_object" },
    });

    const content = response.choices[0]?.message?.content;
    if (!content) {
      throw new Error("No content received from OpenAI");
    }
    const result = JSON.parse(content);
    return {
      successProbability: Math.max(0, Math.min(1, result.successProbability || 0)),
      recommendedGoal: result.recommendedGoal || campaignData.goalAmount,
      marketInsights: result.marketInsights || [],
    };
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : "Unknown error";
    throw new Error("Failed to predict funding success: " + errorMessage);
  }
}

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  console.warn("REPLIT_DOMAINS not set, using development fallback");
}

const getOidcConfig = memoize(
  async () => {
    try {
      return await client.discovery(
        new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
        process.env.REPL_ID!
      );
    } catch (error) {
      console.error("OIDC discovery failed:", error);
      throw new Error("Authentication system configuration error");
    }
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET || "fallback-secret-for-development-only-not-secure",
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  // For now, we'll handle user creation manually as this is a manual auth system
  console.log("User claims received:", claims);
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  // Handle missing REPLIT_DOMAINS for development
  if (!process.env.REPLIT_DOMAINS) {
    console.warn("Skipping OIDC setup - REPLIT_DOMAINS not configured");
    return;
  }

  let config: any = null;

  try {
    config = await getOidcConfig();

    const verify: VerifyFunction = async (
      tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
      verified: passport.AuthenticateCallback
    ) => {
      const userClaims = tokens.claims();
      if (!userClaims) {
        return verified(new Error("No user claims found"));
      }

      const user = {
        id: userClaims.sub as string,
        username: userClaims.preferred_username as string || userClaims.sub as string,
        email: userClaims.email as string,
        password: "", // Not used for OIDC
        firstName: (userClaims.first_name as string) || null,
        lastName: (userClaims.last_name as string) || null,
        profileImageUrl: (userClaims.profile_image_url as string) || null,
        walletAddress: null,
        kycStatus: "pending",
        kycDocuments: null,
        role: "user",
        isFlagged: false,
        flaggedReason: null,
        flaggedBy: null,
        flaggedAt: null,
        profileCompletion: 0,
        joinDate: new Date(),
        createdAt: new Date(),
        updatedAt: null,
      };
      updateUserSession(user, tokens);
      await upsertUser(userClaims);
      verified(null, user);
    };

    for (const domain of process.env.REPLIT_DOMAINS.split(",")) {
      const strategy = new Strategy(
        {
          name: `replitauth:${domain}`,
          config,
          scope: "openid email profile offline_access",
          callbackURL: `https://${domain}/api/callback`,
        },
        verify,
      );
      passport.use(strategy);
    }

    console.log("Authentication setup completed successfully");
  } catch (error) {
    console.error("Failed to setup authentication:", error);
    console.warn("Authentication will not be available");
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    if (!config) {
      return res.status(501).json({ message: "Authentication not configured" });
    }
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    if (!config) {
      return res.status(501).json({ message: "Authentication not configured" });
    }
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    if (!config) {
      req.logout(() => {
        res.redirect("/");
      });
      return;
    }
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated, requireAdmin } from "./auth";
import { 
  optimizeCampaignTitle, 
  enhanceCampaignDescription, 
  analyzeCampaignCredibility,
  predictFundingSuccess 
} from "./openai";
import { insertCampaignSchema, insertContributionSchema, insertTransactionSchema, insertKycApplicationSchema, insertReinstatementRequestSchema, insertUserNotificationSchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  setupAuth(app);

  // Removed duplicate auth route - using the one in auth.ts instead

  // Campaign routes
  app.get('/api/campaigns', async (req, res) => {
    try {
      const { category, status, limit, offset } = req.query;
      const campaigns = await storage.getCampaigns({
        category: category as string,
        status: status as string,
        limit: limit ? parseInt(limit as string) : undefined,
        offset: offset ? parseInt(offset as string) : undefined,
      });
      res.json(campaigns);
    } catch (error) {
      console.error("Error fetching campaigns:", error);
      res.status(500).json({ message: "Failed to fetch campaigns" });
    }
  });

  // Get campaigns by category (more specific route)
  app.get('/api/campaigns/:category', async (req, res) => {
    try {
      const { category } = req.params;
      const { status, limit, offset } = req.query;

      // Check if this is a category or an actual campaign ID (UUIDs are 36 chars)
      if (category.length === 36 && category.includes('-')) {
        // This is likely a UUID, treat as campaign ID
        const campaign = await storage.getCampaign(category);
        if (!campaign) {
          return res.status(404).json({ message: "Campaign not found" });
        }
        return res.json(campaign);
      }

      // This is a category filter
      const campaigns = await storage.getCampaigns({
        category: category,
        status: status as string,
        limit: limit ? parseInt(limit as string) : undefined,
        offset: offset ? parseInt(offset as string) : undefined,
      });
      res.json(campaigns);
    } catch (error) {
      console.error("Error fetching campaigns:", error);
      res.status(500).json({ message: "Failed to fetch campaigns" });
    }
  });

  app.post('/api/campaigns', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const user = await storage.getUser(userId);

      if (!user || user.kycStatus !== "approved") {
        return res.status(403).json({ 
          message: "KYC verification required to create campaigns",
          kycStatus: user?.kycStatus || 'pending'
        });
      }

      const campaignData = insertCampaignSchema.parse({
        ...req.body,
        creatorId: userId,
        status: 'pending_approval', // New campaigns start as pending approval
      });

      const campaign = await storage.createCampaign(campaignData);
      res.status(201).json(campaign);
    } catch (error) {
      console.error("Error creating campaign:", error);
      res.status(400).json({ message: (error as Error).message || "Failed to create campaign" });
    }
  });

  app.put('/api/campaigns/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const user = await storage.getUser(userId);

      if (user?.isFlagged) {
        return res.status(403).json({ message: "Cannot edit campaigns while account is flagged" });
      }

      const campaign = await storage.getCampaign(req.params.id);

      if (!campaign) {
        return res.status(404).json({ message: "Campaign not found" });
      }

      if (campaign.creatorId !== userId) {
        return res.status(403).json({ message: "Unauthorized to update this campaign" });
      }

      // Check if campaign is pending re-approval after edits
      if (campaign.isEditedAfterApproval && campaign.status === "pending_approval") {
        return res.status(403).json({ 
          message: "Cannot edit campaign while pending admin re-approval",
          requiresReview: true 
        });
      }

      // Can't edit rejected campaigns
      if (campaign.status === "rejected") {
        return res.status(403).json({ message: "Cannot edit rejected campaigns" });
      }

      // Determine if this is a critical edit that requires re-approval
      const criticalFields = ['title', 'description', 'goalAmount', 'deadline', 'fundingType', 'category'];
      const isCriticalEdit = criticalFields.some(field => 
        req.body[field] !== undefined && req.body[field] !== campaign[field as keyof typeof campaign]
      );

      let updateData = { ...req.body };

      // If this is a critical edit to an approved campaign, mark for re-approval
      if (isCriticalEdit && campaign.status === "active" && campaign.originalApprovalDate) {
        updateData = {
          ...updateData,
          status: "pending_approval",
          isEditedAfterApproval: true,
          editCount: (campaign.editCount || 0) + 1,
          lastEditedAt: new Date(),
          adminComments: null, // Clear previous admin comments
          reviewedBy: null,
          reviewedAt: null,
        };

        // Create notification for user
        await storage.createUserNotification({
          userId,
          title: "Campaign Under Review",
          message: `Your campaign "${campaign.title}" has been submitted for admin review due to significant changes. The campaign is temporarily paused until approval.`,
          type: "warning",
          relatedCampaignId: campaign.id,
        });
      } else if (!isCriticalEdit) {
        // Minor edit - just update last edited time
        updateData.lastEditedAt = new Date();
      }

      const updatedCampaign = await storage.updateCampaignWithEditTracking(req.params.id, updateData, userId);

      res.json({
        ...updatedCampaign,
        requiresReview: isCriticalEdit && campaign.status === "active" && campaign.originalApprovalDate,
        editType: isCriticalEdit ? "critical" : "minor"
      });
    } catch (error) {
      console.error("Error updating campaign:", error);
      res.status(400).json({ message: (error as Error).message || "Failed to update campaign" });
    }
  });

