                            </Dialog>
                          )}

                          <Dialog open={resetPasswordDialog && selectedUser?.id === user.id} onOpenChange={(open) => {
                            setResetPasswordDialog(open);
                            if (open) setSelectedUser(user);
                            else {
                              setSelectedUser(null);
                              setNewPassword("");
                            }
                          }}>
                            <DialogTrigger asChild>
                              <Button size="sm" variant="outline" data-testid={`button-reset-password-${user.id}`}>
                                <RefreshCw className="h-4 w-4" />
                              </Button>
                            </DialogTrigger>
                            <DialogContent>
                              <DialogHeader>
                                <DialogTitle>Reset User Password</DialogTitle>
                                <DialogDescription>
                                  Set a new password for {user.firstName} {user.lastName}
                                </DialogDescription>
                              </DialogHeader>
                              <div className="space-y-4">
                                <div>
                                  <label className="text-sm font-medium">New Password</label>
                                  <Input
                                    type="password"
                                    value={newPassword}
                                    onChange={(e) => setNewPassword(e.target.value)}
                                    placeholder="Enter new password (min 6 characters)"
                                    data-testid="input-new-password"
                                  />
                                </div>
                                <div className="flex justify-end gap-2">
                                  <Button variant="outline" onClick={() => setResetPasswordDialog(false)}>
                                    Cancel
                                  </Button>
                                  <Button
                                    onClick={() => resetPasswordMutation.mutate({ userId: user.id, newPassword })}
                                    disabled={newPassword.length < 6 || resetPasswordMutation.isPending}
                                    data-testid="button-confirm-reset-password"
                                  >
                                    {resetPasswordMutation.isPending ? "Resetting..." : "Reset Password"}
                                  </Button>
                                </div>
                              </div>
                            </DialogContent>
                          </Dialog>

                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => {
                              window.open(`/api/admin/users/${user.id}/export`, '_blank');
                            }}
                            data-testid={`button-export-user-${user.id}`}
                          >
                            <Download className="h-4 w-4" />
                          </Button>

                          <Dialog open={deleteUserDialog && selectedUser?.id === user.id} onOpenChange={(open) => {
                            setDeleteUserDialog(open);
                            if (open) setSelectedUser(user);
                            else setSelectedUser(null);
                          }}>
                            <DialogTrigger asChild>
                              <Button size="sm" variant="outline" className="text-red-600" data-testid={`button-delete-user-${user.id}`}>
                                <Trash2 className="h-4 w-4" />
                              </Button>
                            </DialogTrigger>
                            <DialogContent>
                              <DialogHeader>
                                <DialogTitle>Delete User Account</DialogTitle>
                                <DialogDescription>
                                  This action cannot be undone. This will permanently delete {user.firstName} {user.lastName}'s account and all associated data.
                                </DialogDescription>
                              </DialogHeader>
                              <div className="flex justify-end gap-2">
                                <Button variant="outline" onClick={() => setDeleteUserDialog(false)}>
                                  Cancel
                                </Button>
                                <Button
                                  variant="destructive"
                                  onClick={() => deleteUserMutation.mutate(user.id)}
                                  disabled={deleteUserMutation.isPending}
                                  data-testid="button-confirm-delete-user"
                                >
                                  {deleteUserMutation.isPending ? "Deleting..." : "Delete Permanently"}
                                </Button>
                              </div>
                            </DialogContent>
                          </Dialog>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="campaigns">
          <Card>
            <CardHeader>
              <CardTitle>Campaign Management</CardTitle>
              <CardDescription>
                Review and manage campaign submissions
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Campaign</TableHead>
                    <TableHead>Creator</TableHead>
                    <TableHead>Goal</TableHead>
                    <TableHead>Raised</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {campaigns.slice(0, 5).map((campaign: any) => (
                    <TableRow key={campaign.id}>
                      <TableCell>
                        <div className="font-medium">{campaign.title}</div>
                      </TableCell>
                      <TableCell>{campaign.creatorId}</TableCell>
                      <TableCell>${campaign.goalAmount}</TableCell>
                      <TableCell>${campaign.currentAmount}</TableCell>
                      <TableCell>
                        <Badge 
                          variant={campaign.status === "active" ? "default" : 
                                  campaign.status === "pending_approval" ? "secondary" : "destructive"}
                        >
                          {campaign.status.replace('_', ' ')}
                        </Badge>
                      </TableCell>
                      <TableCell>
                        <div className="flex items-center gap-2">
                          <Button size="sm" variant="outline">
                            <Eye className="h-4 w-4 mr-1" />
                            View
                          </Button>
                          {campaign.status === "pending_approval" && (
                            <>
                              <Button 
                                size="sm"
                                onClick={() => handleCampaignAction(campaign.id, "approve")}
                              >
                                Approve
                              </Button>
                              <Button 
                                size="sm" 
                                variant="destructive"
                                onClick={() => handleCampaignAction(campaign.id, "reject")}
                              >
                                Reject
                              </Button>
                            </>
                          )}
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="flagged">
          <Card>
            <CardHeader>
              <CardTitle>Flagged Users</CardTitle>
              <CardDescription>
                Manage users with restricted access and review flagging reasons
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>User</TableHead>
                    <TableHead>Email</TableHead>
                    <TableHead>Flagged Reason</TableHead>
                    <TableHead>Flagged By</TableHead>
                    <TableHead>Date Flagged</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {users.filter(user => user.isFlagged).map((user) => (
                    <TableRow key={user.id}>
                      <TableCell>
                        <div className="font-medium">
                          {user.firstName} {user.lastName}
                        </div>
                      </TableCell>
                      <TableCell>{user.email}</TableCell>
                      <TableCell className="max-w-xs">
                        <p className="text-sm truncate" title={user.flaggedReason || ""}>
                          {user.flaggedReason}
                        </p>
                      </TableCell>
                      <TableCell>{user.flaggedBy}</TableCell>
                      <TableCell>
                        {user.flaggedAt ? new Date(user.flaggedAt).toLocaleDateString() : "N/A"}
                      </TableCell>
                      <TableCell>
                        <div className="flex items-center gap-2">
                          <Button
                            size="sm"
                            variant="outline"
                            className="text-green-600"
                            onClick={() => unflagUserMutation.mutate(user.id)}
                            disabled={unflagUserMutation.isPending}
                          >
                            <CheckCircle className="h-4 w-4 mr-1" />
                            Unflag
                          </Button>
                          <Button size="sm" variant="outline">
                            <Eye className="h-4 w-4 mr-1" />
                            View
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>

              {users.filter(user => user.isFlagged).length === 0 && (
                <div className="text-center py-8 text-muted-foreground">
                  <Shield className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p>No flagged users</p>
                  <p className="text-sm">All users currently have normal access</p>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="reinstatements">
          <Card>
            <CardHeader>
              <CardTitle>Reinstatement Requests</CardTitle>
              <CardDescription>
                Review and process user reinstatement requests
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>User</TableHead>
                    <TableHead>Reason</TableHead>
                    <TableHead>Additional Info</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Submitted</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {reinstatementRequests.map((request) => {
                    const user = users.find(u => u.id === request.userId);
                    return (
                      <TableRow key={request.id}>
                        <TableCell>
                          <div className="font-medium">
                            {user ? `${user.firstName} ${user.lastName}` : "Unknown User"}
                          </div>
                          <div className="text-sm text-muted-foreground">
                            {user?.email}
                          </div>
                        </TableCell>
                        <TableCell className="max-w-xs">
                          <p className="text-sm truncate" title={request.reason}>
                            {request.reason}
                          </p>
                        </TableCell>
                        <TableCell className="max-w-xs">
                          <p className="text-sm truncate" title={request.additionalInfo || ""}>
                            {request.additionalInfo || "N/A"}
                          </p>
                        </TableCell>
                        <TableCell>
                          <Badge 
                            variant={request.status === "approved" ? "default" : 
                                    request.status === "pending" ? "secondary" : "destructive"}
                          >
                            {request.status}
                          </Badge>
                        </TableCell>
                        <TableCell>
                          {new Date(request.createdAt || "").toLocaleDateString()}
                        </TableCell>
                        <TableCell>
                          {request.status === "pending" && (
                            <div className="flex items-center gap-2">
                              <Button
                                size="sm"
                                onClick={() => reinstatementMutation.mutate({ id: request.id, status: "approved" })}
                                disabled={reinstatementMutation.isPending}
                              >
                                <CheckCircle className="h-4 w-4 mr-1" />
                                Approve
                              </Button>
                              <Button
                                size="sm"
                                variant="destructive"
                                onClick={() => reinstatementMutation.mutate({ id: request.id, status: "rejected" })}
                                disabled={reinstatementMutation.isPending}
                              >
                                <XCircle className="h-4 w-4 mr-1" />
                                Reject
                              </Button>
                            </div>
                          )}
                          {request.status !== "pending" && (
                            <Badge variant="outline">
                              {request.status === "approved" ? "Approved" : "Rejected"}
                            </Badge>
                          )}
                        </TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>

              {reinstatementRequests.length === 0 && (
                <div className="text-center py-8 text-muted-foreground">
                  <RefreshCw className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p>No reinstatement requests</p>
                  <p className="text-sm">User reinstatement requests will appear here</p>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="kyc">
          <KYCManagement />
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/components/ai/campaign-assist.content:
import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { 
  Lightbulb, 
  TrendingUp, 
  Target, 
  Zap,
  Brain,
  Sparkles,
  BarChart3,
  AlertCircle,
  CheckCircle,
  Loader2
} from "lucide-react";

interface CampaignAssistantProps {
  title?: string;
  description?: string;
  category?: string;
  goalAmount?: number;
  onTitleSuggestion?: (title: string) => void;
  onDescriptionSuggestion?: (description: string) => void;
}

interface AISuggestion {
  type: "info" | "warning" | "success";
  title: string;
  content: string;
  action?: string;
}

export default function CampaignAssistant({ 
  title = "", 
  description = "", 
  category = "", 
  goalAmount = 0,
  onTitleSuggestion,
  onDescriptionSuggestion 
}: CampaignAssistantProps) {
  const { isAuthenticated } = useAuth();
  const { toast } = useToast();
  const [suggestions, setSuggestions] = useState<AISuggestion[]>([]);
  const [credibilityScore, setCredibilityScore] = useState(0);
  const [fundingPrediction, setFundingPrediction] = useState<{
    successProbability: number;
    recommendedGoal: number;
    marketInsights: string[];
  } | null>(null);

  // Title optimization mutation
  const optimizeTitleMutation = useMutation({
    mutationFn: async (title: string) => {
      return await apiRequest("POST", "/api/ai/optimize-title", { title });
    },
    onSuccess: (data) => {
      setSuggestions(prev => [
        ...prev.filter(s => s.title !== "Title Optimization"),
        {
          type: "info",
          title: "Title Optimization",
          content: `AI suggests: "${data.suggestions[0]}" for ${data.engagementBoost} higher engagement`,
          action: "Apply Suggestion"
        }
      ]);
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "Please log in to use AI assistance.",
          variant: "destructive",
        });
        return;
      }
      console.error("Title optimization failed:", error);
    },
  });

  // Description enhancement mutation
  const enhanceDescriptionMutation = useMutation({
    mutationFn: async (data: { description: string; category: string }) => {
      return await apiRequest("POST", "/api/ai/enhance-description", data);
    },
    onSuccess: (data) => {
      setSuggestions(prev => [
        ...prev.filter(s => s.title !== "Description Enhancement"),
        {
          type: "success",
          title: "Description Enhancement",
          content: `AI improved your description focusing on: ${data.keyImprovements.join(", ")}`,
          action: "Apply Enhancement"
        }
      ]);
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "Please log in to use AI assistance.",
          variant: "destructive",
        });
        return;
      }
      console.error("Description enhancement failed:", error);
    },
  });

  // Credibility analysis mutation
  const analyzeCredibilityMutation = useMutation({
    mutationFn: async (campaignData: any) => {
      return await apiRequest("POST", "/api/ai/analyze-credibility", { campaignData });
    },
    onSuccess: (data) => {
      setCredibilityScore(data.score);
      setSuggestions(prev => [
        ...prev.filter(s => s.title !== "Credibility Analysis"),
        ...data.recommendations.map((rec: string, index: number) => ({
          type: data.score > 7 ? "success" : data.score > 5 ? "warning" : "info",
          title: "Credibility Analysis",
          content: rec,
        }))
      ]);
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "Please log in to use AI assistance.",
          variant: "destructive",
        });
        return;
      }
      console.error("Credibility analysis failed:", error);
    },
  });

  // Funding prediction mutation
  const predictFundingMutation = useMutation({
    mutationFn: async (campaignData: any) => {
      return await apiRequest("POST", "/api/ai/predict-funding", { campaignData });
    },
    onSuccess: (data) => {
      setFundingPrediction(data);
      setSuggestions(prev => [
        ...prev.filter(s => s.title !== "Funding Prediction"),
        {
          type: data.successProbability > 0.7 ? "success" : data.successProbability > 0.4 ? "warning" : "info",
          title: "Funding Prediction",
          content: `${Math.round(data.successProbability * 100)}% success probability. Recommended goal: ${data.recommendedGoal} ETH`,
          action: "View Insights"
        }
      ]);
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "Please log in to use AI assistance.",
          variant: "destructive",
        });
        return;
      }
      console.error("Funding prediction failed:", error);
    },
  });

  // Auto-analyze when campaign data changes
  useEffect(() => {
    if (isAuthenticated && title && description && category) {
      const campaignData = {
        title,
        description,
        category,
        goalAmount,
        duration: 30, // Default 30 days
      };

      // Debounce AI calls
      const timer = setTimeout(() => {
        analyzeCredibilityMutation.mutate(campaignData);
        predictFundingMutation.mutate(campaignData);
      }, 1000);

      return () => clearTimeout(timer);
    }
  }, [title, description, category, goalAmount, isAuthenticated]);

  const handleOptimizeTitle = () => {
    if (title) {
      optimizeTitleMutation.mutate(title);
    }
  };

  const handleEnhanceDescription = () => {
    if (description && category) {
      enhanceDescriptionMutation.mutate({ description, category });
    }
  };

  const handleApplySuggestion = (suggestion: AISuggestion) => {
    if (suggestion.title === "Title Optimization" && onTitleSuggestion) {
      // Extract suggested title from content
      const match = suggestion.content.match(/AI suggests: "([^"]+)"/);
      if (match) {
        onTitleSuggestion(match[1]);
        toast({
          title: "Title Updated",
          description: "AI-optimized title has been applied.",
        });
      }
    } else if (suggestion.title === "Description Enhancement" && onDescriptionSuggestion) {
      // This would need the enhanced description from the mutation response
      toast({
        title: "Enhancement Applied",
        description: "Description improvements have been applied.",
      });
    }
  };

  const getSuggestionIcon = (type: string) => {
    switch (type) {
      case "success": return CheckCircle;
      case "warning": return AlertCircle;
      default: return Lightbulb;
    }
  };

  const getSuggestionColor = (type: string) => {
    switch (type) {
      case "success": return "text-cyber-green";
      case "warning": return "text-cyber-yellow";
      default: return "text-cyber-blue";
    }
  };

  const getSuggestionBg = (type: string) => {
    switch (type) {
      case "success": return "bg-cyber-green/10 border-cyber-green/30";
      case "warning": return "bg-cyber-yellow/10 border-cyber-yellow/30";
      default: return "bg-cyber-blue/10 border-cyber-blue/30";
    }
  };

  if (!isAuthenticated) {
    return (
      <Card className="glass-morphism border-white/20">
        <CardContent className="p-6 text-center">
          <Brain className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
          <p className="text-muted-foreground">Log in to access AI assistance</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* AI Assistant Header */}
      <Card className="glass-morphism border-white/20">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <div className="w-8 h-8 bg-gradient-to-r from-cyber-blue to-cyber-purple rounded-full flex items-center justify-center">
              <Brain className="w-4 h-4 text-white" />
            </div>
            AI Campaign Assistant
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Quick Actions */}
          <div className="grid grid-cols-2 gap-3">
            <Button
              variant="outline"
              size="sm"
              onClick={handleOptimizeTitle}
              disabled={!title || optimizeTitleMutation.isPending}
              className="glass-morphism border-cyber-blue/50 hover:bg-cyber-blue/20"
            >
              {optimizeTitleMutation.isPending ? (
                <Loader2 className="w-4 h-4 animate-spin" />
              ) : (
                <Sparkles className="w-4 h-4" />
              )}
              <span className="ml-2">Optimize Title</span>
            </Button>

            <Button
              variant="outline"
              size="sm"
              onClick={handleEnhanceDescription}
              disabled={!description || !category || enhanceDescriptionMutation.isPending}
              className="glass-morphism border-cyber-purple/50 hover:bg-cyber-purple/20"
            >
              {enhanceDescriptionMutation.isPending ? (
                <Loader2 className="w-4 h-4 animate-spin" />
              ) : (
                <Zap className="w-4 h-4" />
              )}
              <span className="ml-2">Enhance Text</span>
            </Button>
          </div>

          {/* Credibility Score */}
          {credibilityScore > 0 && (
            <motion.div
              initial={{ opacity: 0, scale: 0.95 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.3 }}
              className="bg-muted/30 rounded-lg p-4"
            >
              <div className="flex justify-between items-center mb-3">
                <span className="font-medium">Campaign Credibility Score</span>
                <Badge className={credibilityScore > 7 ? "bg-cyber-green/20 text-cyber-green" : 
                                credibilityScore > 5 ? "bg-cyber-yellow/20 text-cyber-yellow" : 
                                "bg-red-500/20 text-red-400"}>
                  {credibilityScore.toFixed(1)}/10
                </Badge>
              </div>
              <Progress 
                value={(credibilityScore / 10) * 100} 
                className="h-2 progress-glow" 
              />
              <div className="text-xs text-muted-foreground mt-2">
                Based on content quality, market analysis, and best practices
              </div>
            </motion.div>
          )}

          {/* Funding Prediction */}
          {fundingPrediction && (
            <motion.div
              initial={{ opacity: 0, scale: 0.95 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.3, delay: 0.1 }}
              className="bg-muted/30 rounded-lg p-4"
            >
              <div className="flex justify-between items-center mb-3">
                <span className="font-medium">Success Probability</span>
                <Badge className={fundingPrediction.successProbability > 0.7 ? "bg-cyber-green/20 text-cyber-green" : 
                                fundingPrediction.successProbability > 0.4 ? "bg-cyber-yellow/20 text-cyber-yellow" : 
                                "bg-red-500/20 text-red-400"}>
                  {Math.round(fundingPrediction.successProbability * 100)}%
                </Badge>
              </div>
              <Progress 
                value={fundingPrediction.successProbability * 100} 
                className="h-2 progress-glow" 
              />
              <div className="text-xs text-muted-foreground mt-2">
                Recommended goal: {fundingPrediction.recommendedGoal} ETH
              </div>
            </motion.div>
          )}
        </CardContent>
      </Card>

      {/* AI Suggestions */}
      {suggestions.length > 0 && (
        <Card className="glass-morphism border-white/20">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TrendingUp className="w-5 h-5 text-cyber-green" />
              AI Recommendations
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {suggestions.map((suggestion, index) => {
              const Icon = getSuggestionIcon(suggestion.type);
              return (
                <motion.div
                  key={`${suggestion.title}-${index}`}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.3, delay: index * 0.1 }}
                  className={`p-4 rounded-lg border ${getSuggestionBg(suggestion.type)}`}
                >
                  <div className="flex items-start gap-3">
                    <Icon className={`w-5 h-5 ${getSuggestionColor(suggestion.type)} flex-shrink-0 mt-0.5`} />
                    <div className="flex-1">
                      <div className={`font-medium mb-1 ${getSuggestionColor(suggestion.type)}`}>
                        {suggestion.title}
                      </div>
                      <div className="text-sm text-muted-foreground mb-3">
                        {suggestion.content}
                      </div>
                      {suggestion.action && (
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => handleApplySuggestion(suggestion)}
                          className="text-xs border-current hover:bg-current/10"
                        >
                          {suggestion.action}
                        </Button>
                      )}
                    </div>
                  </div>
                </motion.div>
              );
            })}
          </CardContent>
        </Card>
      )}

      {/* Market Insights */}
      {fundingPrediction?.marketInsights && fundingPrediction.marketInsights.length > 0 && (
        <Card className="glass-morphism border-white/20">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <BarChart3 className="w-5 h-5 text-cyber-purple" />
              Market Insights
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {fundingPrediction.marketInsights.map((insight, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.3, delay: index * 0.1 }}
                  className="flex items-start gap-3 p-3 bg-muted/30 rounded-lg"
                >
                  <Target className="w-4 h-4 text-cyber-purple flex-shrink-0 mt-0.5" />
                  <div className="text-sm text-muted-foreground">{insight}</div>
                </motion.div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Empty State */}
      {suggestions.length === 0 && !credibilityScore && !fundingPrediction && (
        <Card className="glass-morphism border-white/20">
          <CardContent className="p-8 text-center">
            <Brain className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
            <p className="text-muted-foreground mb-2">AI Assistant Ready</p>
            <p className="text-sm text-muted-foreground">
              Fill in your campaign details to get personalized AI recommendations
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

