import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { useAuth } from "@/hooks/useAuth";
import { 
  Shield, 
  CheckCircle, 
  Clock, 
  AlertTriangle,
  FileText,
  Camera,
  CreditCard,
  Building,
  User,
  XCircle
} from "lucide-react";
import { Link } from "wouter";

interface KYCStatusProps {
